(2611, 'about', 'about', 'About', NULL, 'Home', 'Group', NULL, 3, NULL, NULL, NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0000-00-00', NULL, '1', '0', NULL, NULL, NULL, '2022-11-30 05:57:37', '2022-11-30 05:57:37'),
(2612, 'home_about', 'homeabout', 'WHO ARE WE?', NULL, 'Home', 'Normal', NULL, 1, 'CESIF Nepal is a not-for profit, autonomous and independent private research institution serving in the public interest, with significant independence from any donor or interest group and autonomy from government.', NULL, NULL, NULL, 2611, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-11-30 05:58:18', '2022-12-08 21:42:54'),
(2613, 'THEMATIC-AREAS', 'thematicareas-', 'THEMATIC AREAS', NULL, 'Main', 'Group', NULL, 2, NULL, NULL, NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-11-30 06:11:03', '2022-12-08 16:58:00'),
(2615, 'Domestic-Politics-&-Governanace', 'domesticpoliticsgovernanace', 'Domestic Politics & Governanace', NULL, 'Main', 'Group', NULL, 1, NULL, NULL, NULL, NULL, 2613, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0000-00-00', NULL, '1', '0', NULL, NULL, NULL, '2022-11-30 06:20:41', '2023-01-03 20:15:34'),
(2616, 'Domestic-Politics-&-Governanace-Commentaries', 'domesticpoliticsgovernanacecommentaries', 'Commentaries', NULL, 'Main', 'Group', NULL, 1, NULL, NULL, NULL, NULL, 2615, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-11-30 06:21:07', '2022-12-06 08:33:43'),
(2618, 'Domestic-Politics-&-Governanace-News-Digest', 'domesticpoliticsgovernanacenewsdigest', 'News Digest', NULL, 'Main', 'Group', NULL, 2, NULL, NULL, NULL, NULL, 2615, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0000-00-00', NULL, '1', '0', NULL, NULL, NULL, '2022-11-30 06:48:23', '2022-11-30 06:48:39'),
(2624, 'about-us', 'aboutus', 'About us', NULL, 'Main', 'Group', NULL, 1, NULL, NULL, NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-12-01 05:14:47', '2023-01-03 20:15:07'),
(2625, 'About-CESIF', 'aboutcesif', 'About CESIF', NULL, 'Main', 'Group', NULL, 1, '<h4>CESIF-Nepal</h4>', NULL, 'CESIF Nepal is a not-for profit, autonomous and independent private research institution serving in the public interest, with significant independence from any donor or interest group and autonomy from government.', NULL, 2624, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-12-01 05:15:52', '2022-12-01 05:57:03'),
(2626, 'Vision-Mission', 'visionmission', 'Vision / Mission', NULL, 'Main', 'Normal', NULL, 1, 'Our mission is to help the government and the society create a democratic, secure and just society by making informed and transparent decisions about policies and practices.', NULL, NULL, NULL, 2625, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-12-01 05:16:32', '2022-12-01 05:21:10'),
(2627, 'Strategy', 'strategy', 'Strategy', NULL, 'Main', 'Normal', NULL, 2, '<p>Generate and Communicate:&nbsp;Generate and communicate informed research, analysis and debate about policies and practices to the public and the policymakers in a language that is understandable, reliable and accessible.</p>\r\n\r\n<p>Bridge:&nbsp;Act as a bridge between the academic and policymaking communities and between the state and civil society.</p>\r\n\r\n<p>Serve:&nbsp;Serve in the public interest as an independent and credible voice.</p>', NULL, NULL, NULL, 2625, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-12-01 05:20:53', '2022-12-01 05:20:53'),
(2628, 'our-team', 'ourteam', 'Our Team', NULL, 'Main', 'Group', NULL, 2, NULL, NULL, NULL, NULL, 2624, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-12-01 05:26:32', '2022-12-01 05:26:32'),
(2629, 'VIJAY KANTA KARNA', 'vijay-kanta-karna', 'VIJAY KANT KARNA', NULL, 'Main', 'Team', NULL, 1, 'EXECUTIVE CHAIRPERSON&nbsp;', NULL, 'Former Ambassador to Denmark (2008-2012), Vijay Kant Karna is the Executive Chairperson of the Centre for Social Inclusion and Federalism (CESIF). Ambassador Karna is a professor of political science at Tribhuvan University. His interests include research and discussions on&nbsp;international relations, geopolitics, democracy, cros - border issues and&nbsp;working for the marginalized communities&nbsp;including Madhesis, Dalits, Tharus, Muslims and Janajatis.&nbsp;', NULL, 2628, NULL, NULL, NULL, '/uploads/banner_image/1669872515_vijaya-kanta-karna.jpg', NULL, NULL, NULL, NULL, NULL, NULL, '1', '0', 'https://www.facebook.com/', 'https://www.twitter.com/', 'https://www.instargam.com/', '2022-12-01 05:28:35', '2023-01-06 19:31:36'),
(2630, 'AJAYA BHADRA KHANAL', 'ajaya-bhadra-khanal', 'AJAYA BHADRA KHANAL', NULL, 'Main', 'Team', NULL, 2, 'RESEARCH ADVISOR', NULL, 'Ajaya Bhadra Khanal is the Research Advisor of Centre for Social Inclusion and Federalism (CESIF). He is the Director of Ukaalo Abhiyan and the Editor-in-Chief at Ukaalo.com. He is a former Editor of The Himalayan Times, an English broadsheet daily published from Kathmandu. He has more than 25 years’ experience as a journalist in Nepal in various institutions, capped by inside involvement in conflict transformation. Since the last two years, he has been carrying out research on corruption, impunity, governance and national security interests. His long term interest is academic research and public engagement aimed at enabling communities and national political institutions enforce rule of law and promote democracy.', NULL, 2628, NULL, NULL, NULL, '/uploads/banner_image/1669872941_ajaya-bhadra-khanal.jpg', NULL, NULL, NULL, NULL, NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-12-01 05:35:41', '2023-01-04 16:22:48'),
(2632, 'career', 'career', 'Career', NULL, 'Main', 'Group', NULL, 6, NULL, NULL, NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2022-12', NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-12-01 06:06:57', '2022-12-06 08:36:45'),
(2633, 'Call-for-Finance-&-Admin-Officer', 'callforfinanceadminofficer', 'Call for Finance & Admin Officer.', NULL, 'Main', 'Career', NULL, 2, NULL, NULL, '<h1>Major Responsibilities</h1>\r\n\r\n<h2><span style=\"color:#FF8C00;\">Accounting</span></h2>\r\n\r\n<p><span style=\"color:#008000;\">• Maintain the accounts, following relevant governmental regulations</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Ensure the management of the data file and the appropriate filing of the manual and electronic copies of the financial documents and reports.</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Prepare the monthly financial report based on the accounting application, with all necessary justifying documentation (invoice, bank drafts and bank exchange).</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Ensure a regular monitoring of the bank account(s).</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Carry out the calculation of the payroll, including overtime, for all CESIF staff on the basis of valid contracts.</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Prepare the internal invoices for the personal use of CESIF’s assets.</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Maintain payment record of all invoices received.</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Maintain payee statement records for all suppliers.</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Carry out payments of all regular area office expenses such as office rental, telephone bills, advances, etc.</span></p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">Finance / budget</span>&nbsp;</h2>\r\n\r\n<p><span style=\"color:#008000;\">• Prepare the planning of the financial needs for the forthcoming periods.</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Ensure sufficient availability of funds in the office bank accounts and cash by liaising with the appropriate interlocutors.</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Maintains the petty cash box, with all due accounting and security precautions.</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Prepares the elements for the budget analysis. Maintains the budget forecast tool.</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Monitors the development of expenditure, ensuring that budget overruns do not occur and prepares requests for possible amendments</span>.</p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">Administration</span></h2>\r\n\r\n<p><span style=\"color:#008000;\">• Lead the administrative and logistical aspect of the organization</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Manage office supplies stock and place orders</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Prepare regular reports on expenses and office budgets</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Maintain and update company databases</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Organize a filing system for important and confidential company documents</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Answer queries by employees and clients</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Update office policies as needed</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Maintain a company calendar and schedule appointments</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Distribute and store correspondence (e.g. letters, emails and packages)</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Arrange travel and accommodations as and when necessary</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Provide support in in-house and external events</span></p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">Qualification and Skills Required</span>&nbsp;</h2>\r\n\r\n<p><span style=\"color:#008000;\">• Minimum Bachelor’s Degree in Management (preferably Master’s Degree) with at least two years of working experience in development organizations in admin and finance role</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Sound understanding of taxation rules and government compliance and strong eye for detail</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Experience working with Microsoft Office (Excel in particular) and Tally Software</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Knowledge of budget management</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Sound communication and interpersonal skill</span></p>\r\n\r\n<p><span style=\"color:#008000;\">• Preferably a two-wheeler vehicle</span></p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">To Apply:</span></h2>\r\n\r\n<p>Send a motivation letter mentioning the position applied for and an updated CV including 2 referees with contact number and e-mail address to info@cesifnepal.org no later than, 28th February, 2022.</p>', NULL, 2632, NULL, NULL, NULL, '/uploads/banner_image/1669874866_career.jpg', NULL, NULL, NULL, NULL, '2022-12-06', NULL, '1', '0', NULL, NULL, NULL, '2022-12-01 06:07:46', '2022-12-09 22:05:18'),
(2669, 'RESEARCH-OUTPUTS', 'researchoutputs', 'RESEARCH OUTPUTS', NULL, 'Main', 'Group', NULL, 3, NULL, NULL, NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2022-12', NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-12-06 08:55:36', '2022-12-08 16:57:41'),
(2671, 'Publication', 'publication', 'Publication', NULL, 'Main', 'Group', NULL, 1, NULL, NULL, NULL, NULL, 2669, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2022-12', NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-12-06 09:05:55', '2022-12-06 09:05:55'),
(2674, 'Policy-Briefs', 'policybriefs', 'Policy Briefs', NULL, 'Main', 'Group', NULL, 2, NULL, NULL, NULL, NULL, 2669, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2022-12', '2022-12-06', NULL, '1', '0', NULL, NULL, NULL, '2022-12-06 09:49:58', '2023-01-06 19:49:24'),
(2743, 'Call-for-Junior-Research-Fellow', 'callforjuniorresearchfellow', 'Call for Junior Research Fellow', NULL, 'Main', 'Career', NULL, 1, NULL, NULL, '<p><br />\r\nCESIF Nepal is a not-for profit, autonomous and independent private research institution serving in the public interest, with significant independence from any donor or interest group and autonomy from the government.<br />\r\nCESIF is looking for a qualified candidate for the position of ‘Junior Research Fellow’ for its new research project starting immediately.<br />\r\n<span style=\"color:#FF8C00;\">Qualifications required-</span><br />\r\nIndividuals applying for the position of Junior Researcher should have the following qualifications:<br />\r\n<span style=\"color:#008000;\">• &nbsp;Master’s degree in related field, preferably in International Relations, Political Science, Development Geography, Development Economics.<br />\r\n• &nbsp; Demonstrated competence in research with atleast few years of experience in political-economic research and analysis.<br />\r\n• &nbsp; Good inter-personal and communication skills to deal with a number of diverse stakeholders<br />\r\n• &nbsp; Excellent command in both written and oral English and commendable competence in presentation in Nepali language.<br />\r\n• &nbsp; Publication of articles and research reports is advantageous.</span><br />\r\n<span style=\"color:#FF8C00;\">Major Responsibilities-</span><br />\r\n<span style=\"color:#008000;\">• &nbsp; Support in different research projects; prepare data collection tools, conduct interviews, analyze the data and report writing.<br />\r\n• &nbsp; Conduct desk review and literature review in investigative research topics.<br />\r\n• &nbsp; Engage in strategic communications with citizens, the civil society and policy-makers<br />\r\n• &nbsp; Able to format reports and notes in as per the provided guidelines and organizational formats<br />\r\n• &nbsp; Perform other research and communication activities as required.</span><br />\r\nApplicants can send their CVs along with a cover letter stating why they would make a good candidate to cesifnepal@gmail.com before 10th November 2022 (candidates will be selected on a rolling basis).<br />\r\n&nbsp;</p>', NULL, 2632, NULL, NULL, NULL, '/uploads/banner_image/1670494473_1669874866_career.jpg', NULL, NULL, NULL, '2022-10', NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-12-08 21:55:06', '2022-12-09 22:09:20'),
(2744, 'Call-For-Research-Fellowship', 'callforresearchfellowship', 'Call For Research Fellowship', NULL, 'Main', 'Career', NULL, 3, NULL, NULL, '<p><span style=\"color:#FF8C00;\">CESIF RESEARCH FELLOWSHIP 2022</span><br />\r\nCenter for Social Inclusion and Federalism (CESIF) is a not-for profit, autonomous and independent private research institution serving in the public interest, with significant independence from any interest groups and autonomy from government. CESIF calls for soliciting applications for its flagship program CESIF RESEARCH FELLOWSHIP 2022.<br />\r\nThe fellowship intends to advance the capacity of researchers to flag up contested issues of social inclusion, democracy, geopolitics, Nepal-India-China relations, cross-border migration, domestic politics and federalism among others. With this fellowship program, we hope to help young and talented people to engage in research, analysis and advocacy to create a pool of conscious and informed individuals in the relevant social, political and economic issues.<br />\r\n<span style=\"color:#FF8C00;\">Eligibility criteria</span><br />\r\n<span style=\"color:#000000;\">1. Must have a Masters degree (or final year of master’s degree) in Political Science, International Relations, Development Studies, Sociology/Anthropology, Media Studies, Public Policy, Legal Studies or other relevant subjects.<br />\r\n2. Must be a citizen of Nepal and currently residing in the country.<br />\r\n3. Must be fluent in English and Nepali. Knowledge of local languages as per the research orientation is an added advantage.<br />\r\n4. Must have previous research experience that demonstrates your capacity to undertake the proposed research work and produce quality output within the given timeframe.<br />\r\n5. Must have a high quality analytical and writing skills demonstrated through previous writing samples.</span><br />\r\n<span style=\"color:#FF8C00;\">Potential thematic research areas</span><br />\r\n<span style=\"color:#008000;\">1. &nbsp;Democracy, Good Governance and Rule of Law<br />\r\n2. &nbsp;National Politics and Party Politics<br />\r\n3. &nbsp;Federalism and Social Inclusion<br />\r\n4. &nbsp;International Relations and Geopolitics<br />\r\n5. &nbsp;BRI, IPS, BIMSTEC, BBIN and other initiatives<br />\r\n6. &nbsp;National Security and Foreign Policy</span><br />\r\nHowever, the topics are not strictly confined within the above-mentioned topics and applicants are free to submit concept notes on other relevant themes as mentioned.<br />\r\n<span style=\"color:#FF8C00;\">Selection Procedure</span><br />\r\n<span style=\"color:#000000;\">1. Applicants will be shortlisted based on previous research experience, academic and professional excellence, quality and viability of the research concept note on the first round.<br />\r\n2. The shortlisted candidates are called for interviews and presentation and discussion on their research concept papers.<br />\r\n3. Final selection of the applicants will be done after the interview and presentation of respective concept notes.<br />\r\n4. After the final selection, a supervisor/mentor from the CESIF will be assigned to guide through the research process.<br />\r\n5. A brief training and orientation will be provided before the start of research.</span><br />\r\n<span style=\"color:#FF8C00;\">Terms of Award</span><br />\r\nThe fellowship award will cover a fixed stipend of maximum NRs. 50, 000 for the master students and maximum NRs 160,000 for applicants with masters degree and demonstrated research experience. The stipend will be paid on 2 installments. The maximum fellowship period will be four months. Travel and communication costs are included within the fixed stipend. The allowance is subject to applicable government taxes.<br />\r\n<span style=\"color:#FF8C00;\">Expected deliverables</span><br />\r\n<span style=\"color:#000000;\">1. Throughout this period, the fellow is expected to engage in the proposed and agreed research project and develop concrete research report in consultation with the assigned supervisor.<br />\r\n2. Final deliverables will be research report and other required documentation within the end of agreed period.<br />\r\n3. Presentation and discussion on the final report.<br />\r\n4. Production of policy briefs and Nepali translation of policy brief.<br />\r\n5. Other activities as required for the research during the contract period.</span><br />\r\n<span style=\"color:#FF8C00;\">Application procedure</span><br />\r\nKindly submit your Concept Paper, Curriculum Vitae (CV), Letter of Intent, writing sample and recommendation letter (recommended but not compulsory) not later than 15 March, 2022 at info@cesifnepal.org and gelal.arpan@cesifnepal.org (Please send in both email address).<br />\r\n<br />\r\n&nbsp;</p>', NULL, 2632, NULL, NULL, NULL, '/uploads/banner_image/1670494485_1669874866_career.jpg', NULL, NULL, NULL, '2022-01', NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-12-08 21:58:32', '2022-12-09 22:03:35'),
(2745, 'Call-for-Researchers', 'callforresearchers', 'Call for Researchers', NULL, 'Main', 'Career', NULL, 4, NULL, NULL, '<p>CESIF is looking for (Senior) Researchers, Junior Researchers, and research interns to work in different thematic areas including the following:<br />\r\n<span style=\"color:#008000;\">• &nbsp; International Relations &amp; Foreign Affairs</span><br />\r\n<span style=\"color:#008000;\">• &nbsp; Democracy &amp; Governance<br />\r\n• &nbsp; Strategic and Security Affairs<br />\r\n• &nbsp; Public Policy</span><br />\r\nIndividuals applying for the position of (Senior) Researcher should have the following qualifications:<br />\r\n<span style=\"color:#008000;\">• &nbsp; Masters in related field<br />\r\n• &nbsp; Demonstrated competence in research<br />\r\n• &nbsp; At least five years’ experience researching in the related field<br />\r\n• &nbsp; Preferably, skills in communications and multimedia production</span><br />\r\nIndividuals applying for the position of Junior Researcher should have the following qualifications:<br />\r\n<span style=\"color:#008000;\">• &nbsp; Master’s degree in related field<br />\r\n• &nbsp; Demonstrated competence in research<br />\r\n• &nbsp; Preferably, skills in communications and multimedia production</span><br />\r\nIndividuals applying for the position of Research Interns should have the following:<br />\r\n<span style=\"color:#008000;\">• &nbsp; Bachelor’s degree in related field<br />\r\n• &nbsp; Demonstrated competence in research<br />\r\n• &nbsp; Interest and aptitude in communications and multimedia</span><br />\r\nJob Description for the Researchers (subject to variation)<br />\r\n<span style=\"color:#008000;\">• &nbsp; Monitor developments in the assigned thematic area and produce weekly monitoring reports<br />\r\n• &nbsp; Write monthly analysis on the assigned thematic area<br />\r\n• &nbsp; Conduct (field) research and produce in-depth research reports, papers, or policy analyses on a regular basis<br />\r\n• &nbsp; Engage in strategic communications with citizens, the civil society and policy-makers<br />\r\n• &nbsp; Help produce research briefs and multi-media materials to communicate research findings through multiple platforms (Print, TV, Website, YouTube, Twitter, Facebook, Instagram etc.)<br />\r\n• &nbsp; Conduct monthly presentations on best research practices learned<br />\r\n• &nbsp; Produce opinion pieces or analyses</span><br />\r\n<br />\r\nApplicants can send their CVs along with a cover letter stating why they would make a good candidate to info@cesifnepal.org before 15th February 2022. While applying, mention the position for which you are applying. Also mention the researches you have been a part of and how you contributed to it. Please list/explain the multimedia skills you possess and how you have used these skills in research and communications.<br />\r\n<br />\r\n&nbsp;</p>', NULL, 2632, NULL, NULL, NULL, '/uploads/banner_image/1670494695_1669874866_career.jpg', NULL, NULL, NULL, '2022-01', '2023-01-18', NULL, '1', '0', NULL, NULL, NULL, '2022-12-08 22:03:15', '2022-12-09 21:59:33'),
(2746, 'Call-for-Researchers', 'callforresearchers', 'Call for Researchers', NULL, 'Main', 'Career', NULL, 5, NULL, NULL, '<p><br />\r\nCESIF is looking for (Senior) Researchers, Junior Researchers, and research interns to work in different thematic areas including the following:<br />\r\n<span style=\"color:#008000;\">• &nbsp; Democracy &amp; Governance (including political parties and parliament)<br />\r\n• &nbsp; Security &amp; Strategic Affairs<br />\r\n• &nbsp; Economy and Development</span><br />\r\nIndividuals applying for the position of (Senior) Researcher should have the following qualifications:<br />\r\n<span style=\"color:#008000;\">• &nbsp; Masters in related field<br />\r\n• &nbsp; Demonstrated competence in research<br />\r\n• &nbsp; At least five years’ experience researching and writing in the related field<br />\r\n• &nbsp; Preferably, skills in communications and multimedia production</span><br />\r\nIndividuals applying for the position of Junior Researcher should have the following qualifications:<br />\r\n<span style=\"color:#008000;\">• &nbsp; Master’s degree in related field<br />\r\n• &nbsp; Demonstrated competence in research<br />\r\n• &nbsp; Preferably, skills in communications and multimedia production</span><br />\r\nIndividuals applying for the position of research interns should have the following:<br />\r\n<span style=\"color:#008000;\">• &nbsp; Bachelor’s degree in related field<br />\r\n• &nbsp; Demonstrated competence in research<br />\r\n• &nbsp; Interest and aptitude in communications and multimedia</span><br />\r\nJob description for the researchers (subject to variation)<br />\r\n<span style=\"color:#008000;\">• &nbsp; Monitor developments in the assigned thematic area and produce weekly monitoring reports<br />\r\n• &nbsp; Write monthly analysis on the assigned thematic area<br />\r\n• &nbsp; Conduct (field) research and produce in-depth research reports, papers, or policy analyses on a regular basis<br />\r\n• &nbsp; Engage in strategic communications with citizens, the civil society and policy-makers<br />\r\n• &nbsp; Help produce research briefs and multimedia materials to communicate research findings through multiple platforms (Print, TV, Website, YouTube, Twitter, Facebook, Instagram etc.)<br />\r\n• &nbsp; Conduct monthly presentations on best research practices learned<br />\r\n• &nbsp; Produce opinion pieces or analyses</span><br />\r\n<br />\r\nApplicants can send their CVs along with a cover letter stating why they would make a good candidate to info@cesifnepal.org before 31st July 2021. While applying, mention the position for which you are applying. Also mention the researches you have been a part of and how you contributed to it. Please list/explain the multimedia skills you possess and how you have used these skills in research and communications.</p>', NULL, 2632, NULL, NULL, NULL, '/uploads/banner_image/1670495414_1669874866_career.jpg', NULL, NULL, NULL, '2022-07', NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-12-08 22:05:47', '2022-12-09 21:58:40'),
(2750, 'President’s-Unconstitutional-Move-on-the-Citizenship-Bill', 'presidentsunconstitutionalmoveonthecitizenshipbill', 'President’s Unconstitutional Move on the Citizenship Bill', NULL, 'Main', 'Commentaries', NULL, 1, 'The amendment Bill to the Citizenship Act 2006 was first passed by both the houses and was sent on 5 September 2022 to the President', NULL, '<p>The amendment Bill to the Citizenship Act 2006 was first passed by both the houses and was sent on 5 September 2022 to the President for authentication. The President then sent back the bill to the parliament with 15 different recommendations/ suggestions which was within the prerogative of the President. However, both the houses passed the bill again without making any changes whatsoever. The President is then under the constitutional obligation to pass the bill within 15 days. However, the President failed to do so. This amounts to violation of the constitutional provision on the part of the President. The inability to solve the issue of Citizenship is pushing more and more people into statelessness every year. Thousands of people who are denied the citizenship are deprived of basic privileges offered by the state</p>\r\n\r\n<p>The earlier bill to amend the Nepal Citizenship Act, 2006 had been pending in parliament since 7 August 2018. Despite repetitive discussions, the State Affairs and Good Governance Committee of the House of Representatives were not able to reach an agreement on the bill. Then, the cabinet meeting decided to table a new one by removing the existing one. Home Minister Bal Krishna Khand put forth a withdrawal proposal on July 8 2022 for the bill amending citizenship that had been introduced by the previous CPN-UML administration. The main purpose of the second amendment to the citizenship bill was to address the worries of non-residents and stateless individuals. The National Assembly already passed the bill that the House of Representatives approved.</p>\r\n\r\n<p>The President’s refusal regarding authenticating the bill is facing huge criticisms. Five written petitions were issued against her. The Supreme Court has also issued a show-cause notice, asking for reasons behind her decision. &nbsp;The President is yet to submit it. According to some political observers, the President’s decision to invite party leaders and voice her concerns was improper because the decorum does not provide for such activism.</p>\r\n\r\n<p>The Citizenship Act, on the other hand, contains a few clauses that violate equality rights. For instance, Article 11 (2.b.) states that a child born to a Nepalese mother or father (who holds citizenship) will receive citizenship by descent. Likewise, Article 11(5) states that the person born to a Nepalese mother but an unidentified father can be granted citizenship by descent. This demonstrates a gender disparity and violates a mother’s “Rights to Equality” because it is only women who must first declare that the father is unidentifiable in order for their kid to be given citizenship. The father’s side is not obligated to make any such declarations.<br />\r\n<br />\r\nSome observers believe that &nbsp;since the president is close to CPN- UML, it might be UML’s political motive to garner votes in the upcoming national and provincial polls. The CPN-UML intends to make marriage based naturalization without cooling period an election agenda linking it with nationalism. Each of Nepal’s neighbors has established a certain waiting period before applicants are allowed to submit citizenship applications. The proposed bill is silent on the previous provision of marital naturalized citizenship for foreigners marrying to Nepali men without any cooling period. A section of people are worried that removing the waiting period will make citizenship less valuable.<br />\r\n&nbsp;</p>\r\n\r\n<p>Despite the constitution not allowing for such activism, the experts state that the constitution lacks a “clear provision” stating what will occur if she does not authenticate the bill, the President will neither support the bill in its current form nor resign. Does the bill itself become law if it is not passed? It is unclear what action the relevant authorities should take as a result of the constitutional flaw.</p>', NULL, 2616, NULL, NULL, 'Simran Kharel', '/uploads/banner_image/1670560702_Bidya-bhandari-1.jpg', NULL, NULL, NULL, '2022-10', '2022-10-21', NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 16:23:22', '2023-01-03 21:32:53'),
(2754, 'China-Pushes-for-Communist-Coalition-in-the-Upcoming-Elections', 'chinapushesforcommunistcoalitionintheupcomingelections', 'China Pushes for Communist Coalition in the Upcoming Elections', NULL, 'Main', 'Commentaries', NULL, 2, 'Liu Jianchao, the head of the International Liaison Department of the Chinese Communist Party, visited Nepal on July 10.', NULL, '<p>Liu Jianchao, the head of the International Liaison Department of the Chinese Communist Party, visited Nepal on July 10. The four-day visit of the eight-member team marked the second high-profile visit from Beijing within the span of four months after the ratification of the US’s MCC pact by Nepal in February. Beijing had crossed diplomatic lines in labelling the MCC pact as a “pandora box” and had lobbied to not ratify the pact. Immediately after the ratification of the pact by the parliament, Chinese Foreign Minister Wang Yi visited Nepal in late March. The routine visits of foreign leaders form an integral part of cooperative engagement and visits of Chinese leaders could also be seen in a diplomatic light as an attempt to court Kathmandu. However, it is clear that there is much more to these heightened Chinese engagements than mere diplomatic manoeuvres.</p>\r\n\r\n<p>Beijing hasn’t hidden its interests in uniting the communist parties of Nepal and reinstating the Chinese Communist Party’s (CCP’s) ideological counterpart in Baluwatar. It is, therefore, that the flurry of high-profile visits from China holds significance when talks of the communist coalition are already making rounds within Nepal. Even during this visit, Liu’s team is reported to have urged communist leaders to reconcile their differences and unite once again or at least forge an electoral coalition for the upcoming provincial and federal elections.</p>\r\n\r\n<p>Beijing has perceived the ratification of the MCC pact as evidence of Nepal slipping away from its sphere of influence – and ever since, it has escalated engagements with Nepali politicos to restore the lost influence that Beijing enjoyed while the communist coalition (the NCP) was at the helm of the Kathmandu administration. And to restore the lost influence, Beijing wants the communist forces to reunite again. The ideological affinity between the Chinese Communist Party (CCP) and the communist parties of Nepal makes it easier for Beijing to exert influence over Kathmandu – albeit communism now is largely relegated to a nominal identity by both the Chinese and Nepali communist parties.</p>\r\n\r\n<p>This isn’t the first time that China has gone to length in facilitating the coalition or merger of the Nepali communist parties. The CCP leaders had played an instrumental role back in 2017 in bringing together two main communist parties – CPN UML and CPN Maoist Center – which had led to the formation of the NCP. In 2019, the CCP leaders even trained the leaders of the NCP on “Xi’s thought”. Chinese influence in Nepal had reached its heyday when the NCP was in power. For instance, Nepal had voted in favour of Beijing’s new security law for Hong Kong introduced in the midst of Hong Kong pro-democratic protest – which had provisions of harsh penalties for vaguely defined political crimes– at the UN Human Rights Council (UNHCR).</p>\r\n\r\n<p>Not only did China receive Nepal’s support on the international platforms but also Beijing had greater influence over the internal affairs of Kathmandu as long as the NCP remained in power. Later, when the NCP was on the verge of a split, China through its ambassador Hou Yanqi made several attempts to avert it but to no avail. Then, Chinese foreign minister Wang Yi, during his visit in late March, asserted that China would make concerted efforts to explore the governance model conducive to economic stability and growth. This statement too hinted at facilitating a communist coalition – the statement could be construed as Beijing’s desire to see some replica of the Chinese communist authoritarian governance model in Nepal in the long run. Now that provincial and federal elections are nearby, Liu has asked the communist parties to reunite again.</p>\r\n\r\n<p>In essence, these attempts of China to push for the communist coalition amount to direct interference in the internal affairs of Nepal and cross all diplomatic lines that a nation is supposed to adhere to. The call for jointly exploring a governance model conducive to stability and growth is tantamount to the call for a regime change in a euphemized tone. The call is not only against the diplomatic protocols but also undermines the autonomy of a sovereign nation. After decades of political transition, and a herculean task of state restructuring, Nepal has secured a multiparty democratic governance model as its cherished achievement – and China’s repeated calls to search for a better governance model and direct interferences could become major challenges in our transition to democratic maturity. Further, that China no longer pretends to obfuscate its newfound approaches in interfering with Nepal’s internal affairs doesn’t portend well for the relationship between the two nations.</p>\r\n\r\n<p>On the other front, those issues that China should have prioritized in these high-profile visits have largely been sidelined. The Belt and Road Initiative (BRI) project has been in limbo for years now – and China has shown little to no interest in giving the project the much-needed traction. The agreement of the BRI project with Nepal, its loan component and other issues surrounding the project should have been the major agenda of the visit. Similarly, cooperation between the two nations on multiple fronts – trade, education, health, and environment among others – should have been prioritized. But it appears as if China seems occupied with the intensifying geopolitical rivalry in the region and is making attempts to court Kathmandu again to score a goal against the US-India camp rather than genuinely seeking to deepen the Kathmandu-Beijing ties.</p>', NULL, 2616, NULL, NULL, 'Sourav Dahal', '/uploads/banner_image/1670561141_Liu-Jianchao-the-head-of-the-International-Liaison-Department-of-the-Chinese-Communist-Party-has-arrived-in-Kathmandu-for-a-visit..jpg', NULL, NULL, NULL, '2022-07', '2022-07-15', NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 16:30:41', '2022-12-09 18:41:30'),
(2757, 'Shrinkflation-as-the-coping-mechanism-of-companies-regardless-of-consumer-rights', 'shrinkflationasthecopingmechanismofcompaniesregardlessofconsumerrights', 'Shrinkflation as the coping mechanism of companies regardless of consumer rights', NULL, 'Main', 'Commentaries', NULL, 3, 'The world economy is undergoing the effects of the Covid-19 pandemic and the war in Ukraine which both provoked a shock in demand and supply globally.', NULL, '<h2><span style=\"color:#FF8C00;\">An unfavorable economic context</span></h2>\r\n\r\n<p>The world economy is undergoing the effects of the Covid-19 pandemic and the war in Ukraine which both provoked a shock in demand and supply globally. The annual growth rate of CPI in April 2022 amounted to 9.2% in OECD countries. Nepal’s economy was recovering from the effect of the earthquake and the six-months Indian blockade that both occurred in 2015. This current crisis has further weakened Nepal’s vulnerable economy. Nepal encountered high inflation (Inflation based-CPI jumped to 7.28 % in April 2022 and to 7.87 % last May on y-o-y basis). While Nepal relies mainly on imports and due to the inflation, the import’s price hikes resulted in a depreciation of the NRP as well as a significant compression of foreign reserves. On a micro-economic level, the supply chain encountered constraints due to the rise in production costs.</p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">How ‘less is more’ become a business strategy…</span></h2>\r\n\r\n<p>To face this dreadful economic situation, companies must implement a business strategy to face the curbs in profits. To cope with inflation, companies decided to “downsize” – also referred to as shrinkflation. It consists in reducing products package’s size, which people hardly notice while maintaining the same price. This strategy has been implemented since the 1970s and knew a surge after 2008. This way companies can better cope with production costs surge while avoiding loss of competitiveness. The chore of the strategy lies in that consumer are more price conscious rather than ‘weight conscious’, especially when the labels have been changed in a more discrete one, making the downsizing less obvious. This strategy is used globally by companies in the US, and the UK – where inflation is particularly high (between 6 to 8%). India, also experiencing a similar level of inflation, has seen its companies like Clinic Plus, Parle, or Hindustan Unilever Limited (HUL) implementing this business strategy focusing on poor rural areas.</p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">Why shrinkflation seems to be the safest option for retailers?</span></h2>\r\n\r\n<p>When a company’s production costs increase, it mainly has three options: produce smaller packages, change the ingredients, or raise the price of the final product. In our economic context, changing ingredients can be quite risky. Indeed, changing ingredients means using cheaper ingredients which deteriorates the product’s quality. Another option is to raise prices. Given the current economic health, increasing price is not an option. So, getting packages smaller seems to be the safest for business at the moment.</p>\r\n\r\n<p>On the other side, on the customer’s level, shrinkflation also affects consumers’ shopping habits. Already because of inflation, consumers focus their consumption on the most important items: groceries, electricity, and fuel. Also, as reported in an article of the NPR, some decide pragmatically to buy the whole piece of cheese instead of buying the pre-sliced one. Further, retailers have identified new trends in consumer habits as reported by the Times of India. Downsizing the package makes consumers ‘look for packs that offer value’. This is why, HUL, has decided to create and produce bridge packs (between two price points) ‘to provide the right price-value equation to its consumers, ensuring that products remain affordable and accessible’.</p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">This practice has pushed consumers rights association to speak up …</span></h2>\r\n\r\n<p>Even if this business strategy is not illegal, it remains tricky. For that specific reason, concealing the price hinders consumer rights. This practice affects the ability of the consumer to make informed choices. Indeed, given the small difference in size between the previous and new products, the consumer is not able to notify the change, and thus chose which product is the most suitable for them. This is one of the reasons why some Consumer Protection associations advise looking at the price per kilo instead of the price of the product. In an interview to CESIF, Mr. Hari Prasad Mainali, secretary of the Forum For Protection of Consumer Rights Nepal, restated the Article 44-1 of the Constitution stating that “every consumer shall have the right to obtain quality goods and services”, which rights have been reassured by 2074’s Consumer Act. According to Ratula Chakraborty, professor of business management at the University of EastAnglia, suppliers, and retailers should be obliged to notify the customer that the product has been downsized through a label for a period of at least one month.</p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">… for valid reasons.</span></h2>\r\n\r\n<p>Moreover, companies may use marketing to justify such business strategies: praising the lower environmental cost of the package, standing for the health of the consumers as it limits ‘passive’ overeating, or supporting the innovatoin of the brand for a more efficient product. However, this practice questioned the choices and strategies used by these companies after food giants realized huge profits during the pandemic. Indeed, last may an OXFAM report revealed that 62 new food billionaires have been created over the last two years while global food prices have spiraled by over 30% . Thus, it widely questions the necessity to adopt such practices as shrinkflation. Meanwhile, chairman of Rasna Group, Piruz Khambatta and Sahil Gilani, Gits Food Products Sales &amp; Marketing Director, declared to the Times of India that they won’t be running any promotional offers given they are already absorbing cost inflation. S.Charlebois, professor of Food Distribution and Policy, Dalhouse University, reported his doubts in an article for The Conversation regarding the tendency of this business strategy to contribute to inflation. Indeed, we refer to it as an “invisible inflation” because it challenges statistics to get adjusted sufficiently and efficiently, making this inflation more vicious.</p>', NULL, 2616, NULL, NULL, 'Maela Merrer', '/uploads/banner_image/1670561699_bannershrinkflation.png', NULL, NULL, NULL, '2022-07', '2022-07-07', NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 16:39:59', '2022-12-09 18:41:15'),
(2758, 'Nepal-China-Relations-at-a-Stalemate', 'nepalchinarelationsatastalemate', 'Nepal-China Relations at a Stalemate', NULL, 'Main', 'Commentaries', NULL, 4, 'Chinese investments in Nepal have steadily risen and China has become Nepal’s biggest investment partner in terms of commitment.', NULL, '<p>Chinese investments in Nepal have steadily risen and China has become Nepal’s biggest investment partner in terms of commitment. China has plans to heavily invest in Nepal to enhance infrastructure connectivity and trade and investment, under the framework of the Belt and Road Initiative. However, Chinese actions contradict its commitments towards Nepal.</p>\r\n\r\n<p>Reports say that “China has been creating obstructions in Rasuwagadhi-Kerung and Tatopani border points which is a blatant violation of China’s commitment to bringing them into operation as a major international border under BRI”. The checkpoints at Kerung and Tatopani, which had been closed at the beginning of the Covid-19 pandemic have since reopened, however, traders and locals claim that they are not fully operational. Reports say that the checkpoints which were used to import 200 to 250 truckloads of goods daily are only allowing 20 vehicles at present. While China had earlier cited Covid-19 concerns for restricted transportation, the reopening of checkpoints has not increased the flow of vehicles. Furthermore, trade at the Northern border appears to be happening on Chinese terms with increasing imports and negligible export. Nepali officials have said that the lack of diplomatic initiative from the Nepali government is to blame for the stalemate.</p>\r\n\r\n<p>Nepal-China relations have also suffered blows on the diplomatic front. Nepal is currently embroiled in a border dispute with China wherein local government officials in Humla district of Karnali Province have alleged Chinese encroachment on Nepali territory. Protests over the matter urged the Chinese Embassy in Nepal to release a statement denying the allegations. The Foreign Ministry of Nepal soon released a statement in line with the Chinese Embassy. However, allegations persist with inconsistency among positions held by Nepali authorities. Such occurrences have also led analysts to believe that Nepal-China ties have deteriorated, especially under the new ruling dispensation led by Sher Bahadur Deuba.</p>\r\n\r\n<p>While Nepal’s relationship with India and the United States appears to be thawing despite outstanding bilateral issues, there has been limited engagement with China. Nepal has participated in two disaster response related exercises with the United States and has also participated in a traditional military exercise with India. Similarly, the Nepal-India Bilateral Consultative Group’s meeting on security issues took place under the leadership of Foreign Ministry after being halted for two years. Furthermore, trade with India has smoothened with the ease of restrictions along the southern border. Moreover, the visit of the Secretary-general of BIMSTEC to Nepal in the first month of the year was also perceived as a positive signal towards the rejuvenation of BIMSTEC. Whereas, Nepal has failed to headway on existing commitments and resolve outstanding issues with China.</p>', NULL, 2616, NULL, NULL, 'Shuvangi Poudyal', '/uploads/banner_image/1670561880_Ros-Ktm-2-1-2048x1830.jpg', NULL, NULL, NULL, '2022-02', '2022-02-24', NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 16:43:00', '2022-12-09 18:42:39');
INSERT INTO `navigations` (`id`, `nav_name`, `alias`, `caption`, `caption_nepali`, `nav_category`, `page_type`, `page_template`, `position`, `short_content`, `short_content_nepali`, `long_content`, `long_content_nepali`, `parent_page_id`, `icon_image`, `featured_image`, `icon_image_caption`, `banner_image`, `link`, `main_attachment`, `attachment`, `page_title`, `page_keyword`, `page_description`, `page_status`, `nav_status`, `extra_one`, `extra_two`, `extra_three`, `created_at`, `updated_at`) VALUES
(2759, 'Cracks-Begin-to-Show-in-US-Nepal-Ties', 'cracksbegintoshowinusnepalties', 'Cracks Begin to Show in US-Nepal Ties', NULL, 'Main', 'Commentaries', NULL, 5, 'The US-China relationship has entered a period of great uncertainty and instability as China has become more powerful and more assertive in the international arena. Similarly,&nbsp;', NULL, '<p>The US-China relationship has entered a period of great uncertainty and instability as China has become more powerful and more assertive in the international arena. Similarly, China’s active role in the South Asian region has flared India’s security sensitivities. IPS as a strategy includes a military component, which aims to deter growing Chinese influence in the region. While some argue that IPS and MCC are closely related as outlined in the U.S. National Security Strategy, others maintain that such claims are overblown and that Nepal would be losing out on one of the largest U.S. grants in recent history for the development of energy infrastructure and road maintenance in Nepal.</p>\r\n\r\n<p>Nepal’s political parties and a large section of the public continue to be divided over the program, generating great controversy and forcing the government to put the program on hold. Foreign aid and the influence that come with it has remained a contested topic in geopolitically sensitive Nepal. However, the MCC debate has been politicized, polarizing political parties and the populace. Whether or not MCC is a part of the broader IPS strategy has taken center stage, triggering intense debates on the compact and if it undermines Nepal’s sovereignty and constitution. The “will they, won’t they”, has invited negative reactions domestically and internationally.</p>\r\n\r\n<p>The United States also has good reason to be wary. It has become increasingly apparent that along with domestic politicking, geopolitical influences have affected the decision making process in Nepal. American officials have also raised similar suspicions. A US state department spokesperson has said that The United States believes that China has “actively fomented or encouraged or funded or facilitated” a disinformation campaign in Nepal against a $500-million dollar US grant project”.</p>\r\n\r\n<p>The constant politicization and delay in the compacts’ parliamentary ratification in Nepal has caused frustration among Americans. According to reports, Donald Lu, a top American official, “warned that the failure on the part of Nepal to get the compact ratified by the parliament by the February 28 deadline might result in the United States ‘reviewing’ its relations with Nepal”. American frustration with the continued delay of the compact’s ratification was also apparent during MCC’s Vice President Fatema Sumar’s visit to Nepal in September of last year. However, the messaging this time is firm and while the reorientation of United States’ policies towards Nepal may not be immediate, the fate of the compact is likely to become a deciding factor. However, the change of tone of American officials from persuasive to coercive in their dealing with Nepal suggests that the cracks in US-Nepal relations have begun to show.&nbsp;</p>\r\n\r\n<p>The compact is currently tabled in the parliament awaiting endorsement.</p>', NULL, 2616, NULL, NULL, 'Shuvangi Poudyal', '/uploads/banner_image/1670562110_ROS_KTM_PMDEUBA_USAMINISTER-2025x2048.jpg', NULL, NULL, NULL, '2022-02', '2022-02-23', NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 16:46:50', '2022-12-09 18:43:30'),
(2760, 'News-Digest-Domestic-Politic- &-Governance-(February 3 – 9-2022)', 'newsdigestdomesticpolitic-governancefebruary-3-92022', 'News Digest: Domestic Politics & Governance (February 3 – 9, 2022)', NULL, 'Main', 'News Digest', NULL, 1, 'The government declared local polls for May 13 to be held in a single phase after a meeting between Sher Bahadur Deuba, KP Sharma Oli, and Pushpa Kamal Dahal. Local polls, the MCC grant, and other issues were discussed during the meeting. The United States has set a February 28 deadline for Nepal to ratify MCC in the parliament.', NULL, '<p>&nbsp;</p>\r\n\r\n<p>Confusion has been created about the five year terms of officials elected on June 28 and September 19 elections of 2017. If local polls are held on May 13 this year, new officials would need to be elected, but the previously elected ones would not have finished their five years terms within that date as per constitutional provisions.</p>\r\n\r\n<p>Prakash Sharan Mahat, the former foreign minister, has been elected as spokesperson of the Nepali Congress by party president Sher Bahadur Deuba. Mahat was nominated as a Central Committee member on January 1.</p>\r\n\r\n<p>On February 6, a letter written by Pushpa Kamal Dahal and Sher Bahadur to the MCC headquarters, requesting time for the compact’s ratification in parliament, and another letter by the MCC headquarters setting a deadline of February 28 for parliamentary ratification was leaked to the public domain.</p>\r\n\r\n<p>Kamal Thapa, on February 8, 2022, announced that he will leave the Rastriya Prajatantra Party. Lingden had previously lost the party chair position to Rajendra Lingden, and he had accused former king Gyanendra Shah of interfering with the elections.</p>', NULL, 2618, NULL, NULL, 'CESIF Nepal', '/uploads/banner_image/1670562416_ROS_KTM_PMDEUBA-1-2048x1741.jpg', NULL, NULL, NULL, '2022-02', '2022-02-15', NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 16:51:56', '2022-12-11 17:52:23'),
(2761, 'News-digest-Domestic-Politics-&-Governance-(January-27-to-February 2-2022)', 'newsdigestdomesticpoliticsgovernancejanuary27tofebruary-22022', 'News Digest: Domestic Politics & Governance (January 27 to February 2, 2022)', NULL, 'Main', 'News Digest', NULL, 2, 'During a consultation session with constitutional experts and Election Commission officials, government officials agreed to hold the local level elections before May 19, 2022.', NULL, '<p>During a consultation session with constitutional experts and Election Commission officials, government officials agreed to hold the local level elections before May 19, 2022. Inconsistencies between Section 55 of the Local Governance Act 2017 and provisions in the Constitution had led to confusions about fixing local level election dates.</p>\r\n\r\n<p>The ruling alliance had previously agreed to hold local level elections by mid-June through the amendment of pertinent laws. However, main opposition CPN-UML pointed out that the laws would not need to be amended if the elections were held before the Election Commission’s mandated date of May 18.</p>\r\n\r\n<p>On January 28, during a briefing at Sheetal Niwas, President Bidya Devi Bhandari requested Prime Minister Sher Bahadur Deuba to hold the local level elections in a timely manner as per election laws and the constitution.</p>\r\n\r\n<p>A meeting of the Central Working Committee of Nepali Congress which took place on January 28 decided to form a central work execution committee which is designated to have 37 members. The party’s 14th general convention was held in December 2021, and the recent meeting was the third since the convention concluded.</p>\r\n\r\n<p>A meeting was held between Prime Minister Sher Bahadur Deuba and CPN-Maoist Central Committee Chairman Pushpa Kamal Dahal in Baluwatar. local level election, MCC and other issues were discussed.</p>\r\n\r\n<p>The 19-member secretariat of CPN-UML had a meeting to discuss various issues including local level elections, party organization, remaining district conventions and state conventions. CPN-UML has not held conventions in some districts due to coronavirus. Their General Convention was held last November.</p>', NULL, 2618, NULL, NULL, 'CESIF Nepal', '/uploads/banner_image/1671358869_default-img.jpg', NULL, NULL, NULL, '2022-02', '2022-02-02', NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 17:06:42', '2022-12-27 20:20:34'),
(2762, 'News-Digest-Domestic-Politics-&-Governance-(January-20–26-2022)', 'newsdigestdomesticpoliticsgovernancejanuary20262022', 'News Digest: Domestic Politics & Governance (January 20 – 26, 2022)', NULL, 'Main', 'News Digest', NULL, 3, 'On January 23, the Deuba government consulted with legal and constitutional experts about the conflicting rules of the Local Governance Act and constitutional provisions about setting the date for upcoming local level elections.', NULL, '<p>On January 23, the Deuba government consulted with legal and constitutional experts about the conflicting rules of the Local Governance Act and constitutional provisions about setting the date for upcoming local level elections. The meeting came to a conclusion after the Deuba government decided to amend the Act in order to align with constitutional provisions. If so, local elections originally slated for April-May this year could be delayed.</p>\r\n\r\n<p>On January 24, CPN-UML released a statement, the first since Oli was removed as Prime Minister, in which it criticized the current government on multiple fronts: misinterpreting the constitution to facilitate UML party splits, providing inadequate response to the pandemic, failing to handle the economy, foreign relations, and the MCC pact, among other issues.</p>\r\n\r\n<p>On January 21, a meeting of the high-level political coordination committee discussed fixing dates for the upcoming local level elections. CPN (Unified Socialist), Maoist Centre, and Deuba faction of the Nepali Congress were in favor of postponing the elections beyond the Election Commission’s slated deadline of May 2022, whereas CPN-UML leaders were in favor of holding it by the slated deadline.</p>\r\n\r\n<p>In a meeting of the ruling alliance on January 21, Maoist Centre chairman Pusha Kamal Dahal and CPN (Unified Socialist) chairman Madhav Kumar Nepal proposed delaying local level elections beyond the slated date of April-May. Previous parliamentary and provincial elections were held on November 26 and December 7 of 2017.</p>\r\n\r\n<p>Elections for members of the National Assembly are slated to take place today on January 26. Seats for National Assembly members are divided among parties as follows: Congress (6), Maoist Centre (5), CPN (Unified Socialist) (5), Janata Samajbadi Party (2), Rastriya Janamorcha (1). Candidates from respective parties have been finalised for all 19 seats.</p>', NULL, 2618, NULL, NULL, 'CESIF Nepal', '/uploads/banner_image/1671358953_default-img.jpg', NULL, NULL, NULL, '2022-02', '2022-02-02', NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 17:22:08', '2022-12-18 22:07:33'),
(2763, 'News-Digest-Domestic-Politics-and-Governance-(January-13–19-2022)', 'newsdigestdomesticpoliticsandgovernancejanuary13192022', 'News Digest: Domestic Politics and Governance (January 13 – 19, 2022)', NULL, 'Main', 'News Digest', NULL, 4, 'On January 18, the ruling alliance held a meeting in order to discuss elections. The Election Commission had \r\n proposed elections for April-May.', NULL, '<p>On January 18, the ruling alliance held a meeting in order to discuss elections. The Election Commission had proposed elections for April-May. The Commission’s laws on local elections and constitutional provisions had contradictory positions regarding local laws, and the meeting decided to give priority to the latter.</p>\r\n\r\n<p>CPN-UML criticized the Deuba government for not speaking up on Indian Prime Minister Narendra Modi’s decision to extend and expand a road network through the disputed territory of Lipulekh to Kailash Mansarovar. The Deuba government has not yet responded to Modi’s statement, which was made in December last year.</p>\r\n\r\n<p>Elections for the 19 vacant positions for National Assembly members are to be held on January 26. The election, according to the Local Level Election Act, should be carried out within six months within the end of the term.</p>\r\n\r\n<p>The CPN (Unified Socialist) Party Youth Union has filed a case in the Commission for Investigation of Abuse of Authority demanding investigation into the Lalita Niwas case. The case has demanded that Kumar Regmi and Navin Poudel, who were illegally acquiring land, should be disciplined.</p>\r\n\r\n<p>31 individuals of the judiciary, including a Supreme Court judge and the chief registrar, have been quarantined after being diagnosed for Covid-19.</p>\r\n\r\n<p>After a meeting held at the Ministry of Home Affairs, the government has decided to make it mandatory to show the vaccine card while visiting public places from January 22. The CCMC had also recommended the government to strictly implement this rule.</p>', NULL, 2618, NULL, NULL, 'CESIF Nepal', '/uploads/banner_image/1670564525_ROS_KTM_BALUWATARMEETING-2048x1365.jpg', NULL, NULL, NULL, '2022-01', '2022-01-20', NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 17:27:05', '2022-12-11 17:50:15'),
(2764, 'News-Digest-Domestic-Politic-&-Governance-(January-6–12-2022)', 'newsdigestdomesticpoliticgovernancejanuary6122022', 'News Digest: Domestic Politics & Governance (January 6 – 12, 2022)', NULL, 'Main', 'News Digest', NULL, 5, '<p>Sher Bahadur Deuba, on January 8 and 9, nominated 13 party leaders to the central committee. Most of them had lost contests for office bearer positions.</p>\r\n\r\n<p>On January 5, Shekhar Koirala held a meeting of his political camp, inviting Congress party’s senior party leaders. The agenda of the meeting was to chart out the camp’s strategies, and to discuss ways to check and balance Sher Bahadur Deuba, who has a 80 percent majority in the party.</p>', NULL, '<p>Sher Bahadur Deuba, on January 8 and 9, nominated 13 party leaders to the central committee. Most of them had lost contests for office bearer positions.</p>\r\n\r\n<p>On January 5, Shekhar Koirala held a meeting of his political camp, inviting Congress party’s senior party leaders. The agenda of the meeting was to chart out the camp’s strategies, and to discuss ways to check and balance Sher Bahadur Deuba, who has a 80 percent majority in the party.</p>\r\n\r\n<p>In the meeting of Shekhar Koirala’s political camp of Nepali Congress, Koirala challenged Deuba’s nominations for central committee members, questioning the criteria for the nominations. Leaders also commented that a strategy to check and balance Sher Bahadur Deuba is required as they don’t agree with Deuba’s leadership strategy.</p>\r\n\r\n<p>On January 13, three members of the Communist Party of Nepal’s (Maoist Centre) central secretariat and two people working at the canteen were diagnosed with Covid. The party was scheduled to select 14 of its 15 office bearers, however, this has been postponed as chairman Pushpa Kamal Dahal has suspended meetings until further notice after the covid diagnosis.</p>\r\n\r\n<p>Sher Bahadur Deuba and KP Oli held a meeting at Oli’s residence in Balkot in order to discuss house obstructions by UML and uncertainty about the parliamentary approval of the Millenium Challenge Corporation (MCC).</p>\r\n\r\n<p>Party leaders of Janata Samajbadi Party said that senior leader Baburam Bhattarai criticized Chairman Upendra Yadav for recommending Parshuram Khapung and Uma Shankar Shah as National Assembly candidates without consultation from the party.</p>', NULL, 2618, NULL, NULL, 'CESIF Nepal', '/uploads/banner_image/1670564692_AKJ_INARUWA_SHEKHARKOIRALA.jpg', NULL, NULL, NULL, '2022-01', '2022-02-14', NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 17:29:52', '2022-12-11 17:54:54'),
(2765, 'News-Digest-Domestic-Politics-&-Governance-(December-16–22-2021)', 'newsdigestdomesticpoliticsgovernancedecember16222021', 'News Digest: Domestic Politics & Governance (December 16 – 22, 2021)', NULL, 'Main', 'News Digest', NULL, 6, 'On December 17, CPN-UML Secretary General Shanker Pokhrel announced that Bhim Rawal, who lost to KP Oli', NULL, '<p>On December 17, CPN-UML Secretary General Shanker Pokhrel announced that Bhim Rawal, who lost to KP Oli in elections for party chair in the party’s 10th national congress, has been given a role as a member in the advisory committee of the party.</p>\r\n\r\n<p>On December 17, Nepali Congress declared the remaining five of the eight general secretaries. Through its general convention, the party had elected three joint general secretaries and two general secretaries the day before.</p>\r\n\r\n<p>Gagan Thapa, the former health minister, and Bishwa Prakash Sharma, the party spokesperson, were elected general secretaries of the Nepali Congress. Thapa secured 3,023 votes (64.56 percent), whereas Sharma secured 1,984 votes (42.37 percent).</p>\r\n\r\n<p>Dhanraj Gurung, representing the Shekhar Koirala panel of Nepali Congress, and Purna Bahadur Khadka, representing the Sher Bahadur Deuba panel of Nepali Congress were elected as vice presidents of Nepali Congress.</p>\r\n\r\n<p>Two ordinances have been approved in the meeting of the House of Representatives held on Tuesday amid protests from the main opposition party CPN-UML. Ordinance 2078 to amend Act against Sexual Violence and Ordinance to amend Act related to Criminal Offenses and Criminal Procedure have been approved by Minister for Law, Justice and Parliamentary Affairs Dilendra Prasad Badu.</p>\r\n\r\n<p>Chief Justice Cholendra Shamser Rana convened a meeting of the Judicial Service Commission in order to get the results of the district judges’ examination. The commission had conducted examinations for 18 posts of district judges, held from 12 to 17 September. The commission is under pressure to get the results of the examination.</p>', NULL, 2618, NULL, NULL, 'CESIF NepalCESIF Nepal', '/uploads/banner_image/1671358977_default-img.jpg', NULL, NULL, NULL, '2022-01', '2022-02-14', NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 17:34:16', '2022-12-18 22:07:57'),
(2768, 'Monthly Analysis', 'monthly-analysis', 'Monthly Analysis', NULL, 'Main', 'Group', NULL, 3, NULL, NULL, NULL, NULL, 2615, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 17:53:25', '2022-12-09 17:53:25'),
(2769, 'October-Analysis-Domestic-Politics-and-Governance-monthly', 'octoberanalysisdomesticpoliticsandgovernancemonthly', 'October Analysis: Domestic Politics and Governance', NULL, 'Main', 'Monthly Analysis', NULL, 2, 'As federal and provincial polls are nearby, the month of October saw some dramatic developments – Janata Samajwadi Party (JSP) quitted the ruling', NULL, '<p>Throughout the month of September, the five-party ruling alliance struggled to reach a consensus on seat sharing for the first-past-the-post (FPTP) polls for the upcoming federal and provincial elections. That aside, the upper house of the parliament followed the suit of the lower house in endorsing the Amendment Bill to the Citizenship Act 2006 without incorporating any suggestions of the President. The President, however, didn’t authenticate the bill and then the Supreme Court issued a show-cause notice against her. The Impeachment Recommendation Committee recommended proceeding with the impeachment motion against Chief Justice Cholendra Shumser Rana but now that the tenure of the lower house has already ended, the motion is bound to remain in limbo at least until the new lower house convenes after the November polls.</p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">Timeline of Major Events</span></h2>\r\n&nbsp;\r\n\r\n<table class=\"table table-bordered\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"row\">Date</th>\r\n			<th scope=\"row\">Events</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>3 September</td>\r\n			<td>National Assembly endorsed the Citizenship Amendment Bill again with no changes at all.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>17 September</td>\r\n			<td>Impeachment Recommendation Committee recommended impeaching Rana.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>19 September</td>\r\n			<td>PM Deuba sacked four ministers representing the Janata Samajwadi Party.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>20 September</td>\r\n			<td>Political parties submitted the Proportional Representation List to the Election Commission.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>20 September</td>\r\n			<td>President Bidya Devi Bhandari missed the deadline to authenticate the Citizenship Amendment Bill.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>25 September</td>\r\n			<td>The Supreme Court issued a show-cause notice against President Bidhya Devi Bhandari for not authenticating the citizenship amendment bill.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2><span style=\"color:#FF8C00;\">Parties of the ruling coalition struggle to strike a deal on seat sharing</span></h2>\r\n&nbsp;\r\n\r\n<p>In August, the ruling coalition had set up an 11-member task force to find a framework for sharing seats in the upcoming federal and provincial elections. The task force had decided upon the four criteria of seat sharing. Throughout September, efforts were made over the month to settle the remaining differences – however, that wasn’t to be. The consensus remained elusive, interparty and intraparty rivalry within the ruling coalition intensified; and in the process, the leaders even exchanged barbs</p>\r\n\r\n<p>While there remains a broad agreement on four criteria, each party is striving to ensure a maximum number of seats for itself. The Nepali Congress has remained adamant that the vote share of the local polls should be the primary basis upon which parties could share the seats – and has demanded a hundred seats in its share out of hundred and sixty-five FTTP seats of the federal parliament. The rest of the parties have proposed that the vote share of the 2017 federal and provincial elections instead should be the basis for seat-sharing. This is because the Nepali Congress had emerged as the largest party in the 2022 local polls while it had performed dismally in the 2017 elections. The Maoist Center has asked for sixty FTTP seats, the Unified Socialist forty, Janata Samajwadi thirty-two and the Rashtriya Janamorcha two seats.</p>\r\n\r\n<p>The ruling coalition has met numerous challenges in materializing electoral alliance because reaching a consensus on seat-sharing of a hundred and sixty-five seats between five different parties is in itself a herculean task. Even if parties contest elections on their own, internal management of aspiring candidates is difficult, as there are a large number of aspirants vying for a limited number of seats. With a coalition of five parties now in place, a consensus has to be reached for a single candidate in each constituency from an even larger pool of aspirants. It is, therefore, that the electoral coalition has also intensified intra-party tension – particularly within the Nepali Congress. The faction led by Shekar Koirala organized a “warning program” to warn the establishment of the party not to budge from a hundred-seat demand, and within that provide at least forty seats to the Koirala-led faction. On the opposition front, there have been talks between the CPN UML and Loktantrik Samajwadi Party Nepal to join hands for the polls; however, no concrete decisions are reached yet.</p>\r\n\r\n<p>While coalition governments have been around ever since the restoration of the multiparty democracy in 1990, of late, electoral alliances have also become a new reality in Nepal’s politics. Changing political landscape and the new electoral system have made electoral coalitions more of a necessity than a choice. It is difficult for a party to gain a majority on its own – and therefore parties are going to lengths in forming electoral coalitions even if it means joining hands with rival parties of conflicting ideologies and contrasting voter bases.</p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">President Bhandari’s Partisan Proclivities Continue</span></h2>\r\n&nbsp;\r\n\r\n<p>President Bidya Devi Bhandari missed the deadline of September 20 to authenticate the Amendment Bill to the Citizenship Act 2006. On September 3, the upper house of the parliament followed the suit of the lower house and endorsed the Citizenship Amendment Bill again with no changes at all. President Bidya Devi Bhandari had returned the Citizenship Amendment Bill back to the parliament for a review enlisting fifteen different concerns/suggestions on August 14.</p>\r\n\r\n<p>The president has the prerogative to return the bills passed by both the houses back to the parliament for a review once, but once the parliament sends back the bill after deliberation over the suggestions with or without any making any changes recommended by the President, the President is then under the constitutional obligation to authenticate the bill within 15 days. In failing to authenticate the bill, President Bhandari has violated the constitutional provisions. Five major writ petitions were filed asking for Supreme Court’s intervention in the matter – and on September 15, the Supreme Court issued a show-cause notice against the President.</p>\r\n\r\n<p>President Bhandari’s move seems politically motivated with an intention of weaponizing the amendment bill to facilitate her former party CPN UML in the upcoming provincial and federal polls. The amendment bill has long been dragged into controversy – and attempts have been made mainly by the main opposition CPN UML to make it a major electoral agenda by framing the bill as an issue concerning nationality and sovereignty. With the ruling alliance joining hands for the polls, the electoral prospects of the CPN UML have weakened – and the party was in a search of a sellable electoral agenda. The CPN UML has now labelled the ruling alliance as “anti-nationalist” citing some provisions of the amendment bill with an ulterior motive of cashing electoral success in the forthcoming polls. President Bhandari has sought to blow this issue out of proportion by not authenticating the bill – and in doing so, has buttressed her former party’s effort. The motive is also evident from the fact that when the erstwhile KP Sharma Oli-led government had brought an ordinance with similar provisions, President had shown no reluctance in endorsing it.</p>\r\n\r\n<p>The Constitution of Nepal 2015 envisions President as the protector and patron of the constitution; however, President Bhandari has failed to even live up to the bare minimum standards of being non-partisan. In these formative days of Nepal’s republican system, it is imperative that the President set up a culture – that of strictly abiding by the constitution – to establish the institution of the Presidency as a cherished one within the system. However, President Bhandari’s proclivities over her two-time tenure have tainted the institution of the Presidency with partisan stains, and have served to reduce public confidence and trust in the system as a whole.</p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">Crisis in Judiciary Prolongs</span></h2>\r\n\r\n<p>On September 17, the Impeachment Recommendation Committee submitted its report to the speaker of the Lower House Agni Prasad Sapkota with a conclusion that there are ample grounds to impeach Chief Justice Rana. Those opposition members in the committee, however, registered an eight-point note of dissent against the recommendation to impeach Rana. While quizzed, Chief Justice Rana defended himself with a claim that he hasn’t committed any wrongdoings whatsoever. Rana rather pointed fingers with incendiary remarks against political leaders, particularly that of the ruling alliance, along with other judges, advocates, media and business houses – and accused that all those who are for impeaching him have been responsible for plaguing the judicial system of the country.</p>\r\n\r\n<p>The Impeachment Recommendation Committee submitted its report just a few hours before the five-year tenure of the lower house ended. Now that the tenure of the lower house is over, further proceedings of the impeachment motion can’t be undertaken. This has prolonged the months-long judicial crisis at least until the new lower house resumes after the federal polls slated for this November. This reflects brazen irresponsibility on the part of the ruling alliance – particularly the Deuba-led government as it is now clear that the government wants to keep the impeachment motion in limbo until the tenure of Chief Justice Rana gets over. On the other side, there has been some legal confusion – if or not the impeachment is rendered null and void now that the tenure of the lower house is over. Seizing upon this, Chief Justice Rana made attempts to return back to the Supreme Court and resume his duty. The face-off between the government and suspended Chief Justice Rana escalated to the point that Chief Rana wasn’t allowed to leave his official residence for a few days to stop him from “unconstitutionally” resuming his role. The impeachment motion against the Chief Justice is a matter of utmost importance and the only responsible move of the government would have been to prioritize accordingly. In keeping the impeachment in limbo for months, the Deuba-led government too has played an equal role, if not more, in further exacerbating the already-existing judicial crisis.</p>', NULL, 2768, NULL, NULL, 'Sourav Dahal', '/uploads/banner_image/1670566500_Election_Commission1667209331_1024.jpg', NULL, NULL, NULL, '2022-10', '2022-10-20', NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 18:00:00', '2023-01-05 16:57:45'),
(2772, 'September-Analysis-Domestic-Politics-and-Governance', 'septemberanalysisdomesticpoliticsandgovernance', 'September Analysis: Domestic Politics and Governance', NULL, 'Main', 'Monthly Analysis', NULL, 2, 'Throughout the month of September, the five-party ruling alliance struggled to reach a consensus on seat sharing for the first-past', NULL, '<p>Throughout the month of September, the five-party ruling alliance struggled to reach a consensus on seat sharing for the first-past-the-post (FPTP) polls for the upcoming federal and provincial elections. That aside, the upper house of the parliament followed the suit of the lower house in endorsing the Amendment Bill to the Citizenship Act 2006 without incorporating any suggestions of the President. The President, however, didn’t authenticate the bill and then the Supreme Court issued a show-cause notice against her. The Impeachment Recommendation Committee recommended proceeding with the impeachment motion against Chief Justice Cholendra Shumser Rana but now that the tenure of the lower house has already ended, the motion is bound to remain in limbo at least until the new lower house convenes after the November polls.</p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">Timeline of Major Events</span></h2>\r\n\r\n<table class=\"table table-bordered\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"row\">Date</th>\r\n			<th scope=\"row\">Events</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>3 September</td>\r\n			<td>National Assembly endorsed the Citizenship Amendment Bill again with no changes at all.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>17 September</td>\r\n			<td>Impeachment Recommendation Committee recommended impeaching Rana.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>19 September</td>\r\n			<td>Political parties submitted the Proportional Representation List to the Election Commission.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>20 September</td>\r\n			<td>President Bidya Devi Bhandari missed the deadline to authenticate the Citizenship Amendment Bill.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>25 September/td&gt;</td>\r\n			<td>The Supreme Court issued a show-cause notice against President Bidhya Devi Bhandari for not authenticating the citizenship amendment bill.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2><span style=\"color:#FF8C00;\">Parties of the ruling coalition struggle to strike a deal on seat sharing</span></h2>\r\n&nbsp;\r\n\r\n<p>In August, the ruling coalition had set up an 11-member task force to find a framework for sharing seats in the upcoming federal and provincial elections. The task force had decided upon the four criteria of seat sharing. Throughout September, efforts were made over the month to settle the remaining differences – however, that wasn’t to be. The consensus remained elusive, interparty and intraparty rivalry within the ruling coalition intensified; and in the process, the leaders even exchanged barbs</p>\r\n\r\n<p>While there remains a broad agreement on four criteria, each party is striving to ensure a maximum number of seats for itself. The Nepali Congress has remained adamant that the vote share of the local polls should be the primary basis upon which parties could share the seats – and has demanded a hundred seats in its share out of hundred and sixty-five FTTP seats of the federal parliament. The rest of the parties have proposed that the vote share of the 2017 federal and provincial elections instead should be the basis for seat-sharing. This is because the Nepali Congress had emerged as the largest party in the 2022 local polls while it had performed dismally in the 2017 elections. The Maoist Center has asked for sixty FTTP seats, the Unified Socialist forty, Janata Samajwadi thirty-two and the Rashtriya Janamorcha two seats.</p>\r\n\r\n<p>The ruling coalition has met numerous challenges in materializing electoral alliance because reaching a consensus on seat-sharing of a hundred and sixty-five seats between five different parties is in itself a herculean task. Even if parties contest elections on their own, internal management of aspiring candidates is difficult, as there are a large number of aspirants vying for a limited number of seats. With a coalition of five parties now in place, a consensus has to be reached for a single candidate in each constituency from an even larger pool of aspirants. It is, therefore, that the electoral coalition has also intensified intra-party tension – particularly within the Nepali Congress. The faction led by Shekar Koirala organized a “warning program” to warn the establishment of the party not to budge from a hundred-seat demand, and within that provide at least forty seats to the Koirala-led faction. On the opposition front, there have been talks between the CPN UML and Loktantrik Samajwadi Party Nepal to join hands for the polls; however, no concrete decisions are reached yet.</p>\r\n\r\n<p>While coalition governments have been around ever since the restoration of the multiparty democracy in 1990, of late, electoral alliances have also become a new reality in Nepal’s politics. Changing political landscape and the new electoral system have made electoral coalitions more of a necessity than a choice. It is difficult for a party to gain a majority on its own – and therefore parties are going to lengths in forming electoral coalitions even if it means joining hands with rival parties of conflicting ideologies and contrasting voter bases.</p>\r\n\r\n<h2>President Bhandari’s Partisan Proclivities Continue</h2>\r\n\r\n<p>President Bidya Devi Bhandari missed the deadline of September 20 to authenticate the Amendment Bill to the Citizenship Act 2006. On September 3, the upper house of the parliament followed the suit of the lower house and endorsed the Citizenship Amendment Bill again with no changes at all. President Bidya Devi Bhandari had returned the Citizenship Amendment Bill back to the parliament for a review enlisting fifteen different concerns/suggestions on August 14.</p>\r\n\r\n<p>The president has the prerogative to return the bills passed by both the houses back to the parliament for a review once, but once the parliament sends back the bill after deliberation over the suggestions with or without any making any changes recommended by the President, the President is then under the constitutional obligation to authenticate the bill within 15 days. In failing to authenticate the bill, President Bhandari has violated the constitutional provisions. Five major writ petitions were filed asking for Supreme Court’s intervention in the matter – and on September 15, the Supreme Court issued a show-cause notice against the President.</p>\r\n\r\n<p>President Bhandari’s move seems politically motivated with an intention of weaponizing the amendment bill to facilitate her former party CPN UML in the upcoming provincial and federal polls. The amendment bill has long been dragged into controversy – and attempts have been made mainly by the main opposition CPN UML to make it a major electoral agenda by framing the bill as an issue concerning nationality and sovereignty. With the ruling alliance joining hands for the polls, the electoral prospects of the CPN UML have weakened – and the party was in a search of a sellable electoral agenda. The CPN UML has now labelled the ruling alliance as “anti-nationalist” citing some provisions of the amendment bill with an ulterior motive of cashing electoral success in the forthcoming polls. President Bhandari has sought to blow this issue out of proportion by not authenticating the bill – and in doing so, has buttressed her former party’s effort. The motive is also evident from the fact that when the erstwhile KP Sharma Oli-led government had brought an ordinance with similar provisions, President had shown no reluctance in endorsing it.</p>\r\n\r\n<p>The Constitution of Nepal 2015 envisions President as the protector and patron of the constitution; however, President Bhandari has failed to even live up to the bare minimum standards of being non-partisan. In these formative days of Nepal’s republican system, it is imperative that the President set up a culture – that of strictly abiding by the constitution – to establish the institution of the Presidency as a cherished one within the system. However, President Bhandari’s proclivities over her two-time tenure have tainted the institution of the Presidency with partisan stains, and have served to reduce public confidence and trust in the system as a whole.</p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">Crisis in Judiciary Prolongs</span></h2>\r\n\r\n<p>On September 17, the Impeachment Recommendation Committee submitted its report to the speaker of the Lower House Agni Prasad Sapkota with a conclusion that there are ample grounds to impeach Chief Justice Rana. Those opposition members in the committee, however, registered an eight-point note of dissent against the recommendation to impeach Rana. While quizzed, Chief Justice Rana defended himself with a claim that he hasn’t committed any wrongdoings whatsoever. Rana rather pointed fingers with incendiary remarks against political leaders, particularly that of the ruling alliance, along with other judges, advocates, media and business houses – and accused that all those who are for impeaching him have been responsible for plaguing the judicial system of the country.</p>\r\n\r\n<p>The Impeachment Recommendation Committee submitted its report just a few hours before the five-year tenure of the lower house ended. Now that the tenure of the lower house is over, further proceedings of the impeachment motion can’t be undertaken. This has prolonged the months-long judicial crisis at least until the new lower house resumes after the federal polls slated for this November. This reflects brazen irresponsibility on the part of the ruling alliance – particularly the Deuba-led government as it is now clear that the government wants to keep the impeachment motion in limbo until the tenure of Chief Justice Rana gets over. On the other side, there has been some legal confusion – if or not the impeachment is rendered null and void now that the tenure of the lower house is over. Seizing upon this, Chief Justice Rana made attempts to return back to the Supreme Court and resume his duty. The face-off between the government and suspended Chief Justice Rana escalated to the point that Chief Rana wasn’t allowed to leave his official residence for a few days to stop him from “unconstitutionally” resuming his role. The impeachment motion against the Chief Justice is a matter of utmost importance and the only responsible move of the government would have been to prioritize accordingly. In keeping the impeachment in limbo for months, the Deuba-led government too has played an equal role, if not more, in further exacerbating the already-existing judicial crisis.</p>', NULL, 2768, NULL, NULL, 'Sourav Dahal', '/uploads/banner_image/1671358711_Ruling-coalition-meeting.jpg', NULL, NULL, NULL, '2022-09', '2022-10-20', NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 19:31:43', '2022-12-18 22:03:31'),
(2773, 'thegoal', 'thegoal', 'The Goal', NULL, 'Main', 'Normal', NULL, 3, '1. &nbsp;Research and Analysis<br />\r\n• &nbsp; Develop expertise on the five thematic areas for the first five years.<br />\r\n• &nbsp; Generate high quality research and analysis.<br />\r\n2. &nbsp;Communication and Public Engagement<br />\r\n• &nbsp; Effectively communicate to the public and to policy-makers.<br />\r\n• &nbsp; Engage and bring together the academic and policy-making communities, the state and civil society on public policy research, analysis and debate.<br />\r\n• &nbsp; Carry out a flagship dialogue event every year for branding and visibility.<br />\r\n3. &nbsp;Collaboration and Partnership: Collaborate and partner with other think tanks in Nepal and Asia (e.g., to help communicate their research, to participate in each other’s events, or to exchange ideas or learnings).<br />\r\n4. &nbsp;Others<br />\r\n• &nbsp; Recruit or work with the sharpest and most informed minds available in Nepal and elsewhere.<br />\r\n• &nbsp; Makes use of latest information and technology.<br />\r\n• &nbsp; Makes use of monitoring, evaluation and learning.', NULL, NULL, NULL, 2625, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 20:09:17', '2022-12-09 20:22:49');
INSERT INTO `navigations` (`id`, `nav_name`, `alias`, `caption`, `caption_nepali`, `nav_category`, `page_type`, `page_template`, `position`, `short_content`, `short_content_nepali`, `long_content`, `long_content_nepali`, `parent_page_id`, `icon_image`, `featured_image`, `icon_image_caption`, `banner_image`, `link`, `main_attachment`, `attachment`, `page_title`, `page_keyword`, `page_description`, `page_status`, `nav_status`, `extra_one`, `extra_two`, `extra_three`, `created_at`, `updated_at`) VALUES
(2774, 'August-Analysis-Domestic-Politics-and-Governance', 'augustanalysisdomesticpoliticsandgovernance', 'August Analysis: Domestic Politics and Governance', NULL, 'Main', 'Monthly Analysis', NULL, 3, 'This month, the government announced federal and provincial elections for November 20, 2022. The ruling coalition set up a task force to decide on seat-sharing between the five parties.', NULL, '<p>This month, the government announced federal and provincial elections for November 20, 2022. The ruling coalition set up a task force to decide on seat-sharing between the five parties. The CPN Maoist Center and CPN Unified Socialist decided to contest the election with a single manifesto – while Baburam Bhattarai and Bamdev Gautam agreed with CPN Maoist Center to share the latter’s election symbol. Election talks aside, President Bidya Devi Bhandari returned the Citizenship Amendment Bill to the parliament for review but the lower house endorsed the bill again without any changes, and the upper house is still holding deliberations over it. The Parliament Secretariat forwarded the impeachment motion against Chief Justice Cholendra Shumsher Rana to the Impeachment Recommendation Committee after six months of its registration, and the committee quizzed Chief Justice Rana over the allegations.</p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">Timeline of Major Events</span></h2>\r\n&nbsp;\r\n\r\n<table class=\"table table-bordered\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"row\">Date</th>\r\n			<th scope=\"row\">Events</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>August 4</td>\r\n			<td>The government announced federal and provincial elections for November 20.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>August 5</td>\r\n			<td>The ruling coalition set up an 11-member task force to find out a framework for sharing seats in the upcoming federal and provincial elections.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>August 7</td>\r\n			<td>The Parliament Secretariat forwarded the impeachment motion against Chief Justice Cholendra Shumsher Rana to the Impeachment Recommendation Committee.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>August 14</td>\r\n			<td>President Bidya Devi Bhandari returned the Citizenship Amendment Bill back to the parliament for review.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>August 18</td>\r\n			<td>The Lower House endorsed the Citizenship Amendment Bill without making any changes.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>August 22</td>\r\n			<td>CPN Maoist Center and CPN Unified Socialist decided to contest the election with the same election manifesto.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>August 31</td>\r\n			<td>Impeachment Recommendation Committee quizzed Chief Justice Cholendra Sumsher Rana.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2><span style=\"color:#FF8C00;\">Upcoming Elections and Electoral Coalitions</span></h2>\r\n&nbsp;\r\n\r\n<p>On August 4, the government announced to hold the general and provincial elections on November 20. And then immediately a day after, on August 5, the ruling coalition set up an 11-member task force to find out a framework for sharing seats in the upcoming federal and provincial elections. The task force consisted of three members from the Nepali Congress, and two each from the rest of the five-party alliance. On August 15, the task force reached a consensus on four criteria for seat sharing: (i) FPTP seats won in the 2017 elections, (ii) PR seats won in the 2017 elections, (iii) performance in last May’s local election, and (iv) priority to top leaders of the parties. Later, it was decided that the main decisions of seat-sharing were to be finalized not by the task force but rather by the top leaders themselves. As of now, the Nepali Congress is bargaining for a hundred seats, the Maoist Center sixty, the Unified Socialist forty, Janata Samajwadi thirty-two and the Rashtriya Janamorcha two seats. It is expected that the consensus will be eventually reached such that the Nepali Congress will field around eighty-five to ninety candidates, CPN Maoist Center around fifty, CPN Unified Socialist fifteen, People’s Socialist Party fifteen and CPN Jana Morcha one candidate.</p>\r\n\r\n<p>Now that the ruling alliance has almost agreed on seat-sharing, the month-long speculations regarding the reconfiguration of alliances for the upcoming polls have almost come to an end. The communist coalition including the CPN UML had been making buzz around for months – particularly because in 2017, the CPN Maoist Center had eventually joined hands with the CPN UML for the federal and provincial elections after forging an electoral coalition with Nepali Congress for the local polls. This time around, the communist forces and the Nepali Congress are plunging into the election together – despite the odds that the communist coalition could have easily translated into electoral success compared to this alliance of ruling parties with conflicting ideologies and contrasting voter bases.</p>\r\n\r\n<p>The coalition marks a continuation of the electoral alliance formed for the local polls held last May. While the Nepali Congress and the CPN Maoist Center had ripped off the maximum benefit of the electoral coalition, other parties hadn’t benefited in equal proportion. This time around, both the Nepali Congress and the communist parties of the alliance have formed a strategy to carve out maximum benefit from the electoral collaboration. The Nepali Congress has aimed at emerging as the clear winner with a margin such that the nearest rival would remain far off in total seats won while the rest of the parties are making attempts to collectively put leverage upon the Nepal Congress to ensure a maximum number of seats for themselves. To this end, the communist parties of the ruling alliance also formed a Socialist Center last month. On 22 August, two main parties of the Socialist Center – the CPN Maoist Center and the CPN Unified Socialist decided to contest the election with a single manifesto. Further, Baburam Bhattarai who recently split the People’s Socialist Party to form Nepal Socialist Party and Bamdev Gautam, who has been on a mission to unite communist forces, have reached an agreement with the Maoist Center to contest the election under the symbol of the CPN Maoist Center.</p>\r\n\r\n<p>The electoral coalition however is likely to intensify the intraparty feuds within the big parties of the alliance, particularly the Nepali Congress and the CPN Maoist Center. Even if parties weren’t to form an electoral coalition, internal management of aspirant candidates would be a challenge because a large number of leaders is vying to contest elections from each constituency. With the electoral coalition now in place, there has to be an inter-party consensus candidate from an even larger set of aspirants. The intraparty tension, regarding the same, is already rife within Nepali Congress. The Shekhar Koirala faction which has remained against electoral coalitions has now upped its ante against the establishment faction of the party.This is to ensure that as many leaders from the faction are ensured with the election tickets. The case is similar to the rest of the parties of the ruling alliance.</p>\r\n\r\n<p>While the talks of an opposition coalition between the CPN UML, Kamal Thapa-led Rashtriya Prajatantra Party and the rest have been making rounds – there hasn’t been any concrete development yet.</p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">President’s Partisan Proclivity in the Citizenship Act 2006 Amendment Bill</span></h2>\r\n&nbsp;\r\n\r\n<p>President Bidya Devi Bhandari, on August 14, returned the Citizenship Act 2006 Amendment Bill back to the parliament for a review enlisting fifteen different concerns/suggestions on the 15th day she received it from the Speaker. The bill which was endorsed by both the houses was sent to the Sheetal Niwas for authentication on July 31. However, on 18 August, the lower house endorsed the bill again without making any changes whatsoever. The President has the prerogative of returning the bill back to the parliament for review but if the upper house also chooses not to make further changes to it following the suit of the lower house, the President then will have no other constitutional option other than to authenticate the bill as it is.</p>\r\n\r\n<p>President Bhandari’s move to send back the amendment bill was a constitutional one, nevertheless, it has attracted controversy – and rightly so. This is because President has resorted to partisan proclivities time and again. For instance – while the erstwhile PM KP Sharma Oli dissolved the lower house twice unconstitutionally, President Bhandari had gone to length in facilitating those unconstitutional moves and later she had even made attempts to prevent the split of the Nepal Communist Party (NCP) which was formed with the unification of the CPN UML and the CPN Maoist Center. Even in this case of the amendment bill, President Bhandari’s position resembles close to that of the CPN UML – the party she belonged to before her ascendency to the role of president.</p>\r\n\r\n<p>It is also reported that President Bhandari took advice from unauthorized people regarding the amendment bill– the constitution clearly states that the Attorney General and the cabinet ministers are the ones that the President has to rely on for advice. Further, once the lower house endorsed the bill again making no changes, President then met with retired and incumbent army officials including the chief of Army staff Prabhu Ram Sharma. This flurry of consultations with unauthorized people and army officials isn’t in line with the decorum that the President is supposed to maintain. When President Bhandari has no alternative but to authenticate the bill once the parliament holds deliberations over her suggestions and chooses either or not to make any further changes – barring her resignation from the post – the prudent course of action on her part would have been to avoid such controversial meetups. On the contrary, President Bhandari’s heightened activism – seemingly in preparation to not authenticate the bill– marks yet another partisan proclivity among numerous in her two-time tenure.</p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">Impeachment Motion against the Chief Justice</span></h2>\r\n&nbsp;\r\n\r\n<p>On August 7, the Parliament Secretariat forwarded the impeachment motion against Chief Justice Cholendra Shumsher Rana to the Impeachment Recommendation Committee after six months of its registration. Chief Justice Rana has been in suspension after lawmakers of the ruling coalition filed an impeachment motion on February 13 charging him with twenty-one different allegations. On March 15, the Deuba government had abruptly decided to prorogue the winter session of the parliament just a day before the House was supposed to hold deliberations over the impeachment motion. Ever since then, the impeachment motion has been in limbo and the judicial crisis has been prolonged for months.</p>\r\n\r\n<p>This delay reflects irresponsibility on the part of the government and the ruling coalition. It is imperative that impeachment against the Chief Justice be prioritized and parliamentary decision over it be sought promptly – which was not to be. Now that the Impeachment Recommendation Committee has already quizzed Chief Justice Rana on August 31st, and has proposed the deadline of September 14 to review the impeachment motion, Judiciary can be expected to get the much-needed relief soon.</p>', NULL, 2768, NULL, NULL, 'Sourav Dahal', '/uploads/banner_image/1670574869_Cholendra.jpg', NULL, NULL, NULL, '2022-08', '2022-10-18', NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 20:19:29', '2022-12-11 23:02:18'),
(2775, 'objective', 'objective', 'Objective', NULL, 'Main', 'Normal', NULL, 4, 'Conduct research on five thematic areas:<br />\r\n1. &nbsp;Keep track of latest developments in the thematic areas by tracking media reports, government documents, and, where necessary, conducting direct interviews with experts/stakeholders and taking part in related seminars and workshops. Analyze developments on a weekly basis based on the weekly updates and timelines. Generate policy questions.<br />\r\n2. &nbsp;Select, after collective discussion, a topic for further research. Conduct a literature review and prepare a background document.<br />\r\n3. &nbsp;Collect further data and conduct interviews to prepare a policy brief, research report or a working paper.<br />\r\nFoster interactive and constructive dialogue between researchers, professionals, policy-makers, opinion leaders and activists.<br />\r\nDialogue and Events<br />\r\n1. &nbsp;Flagship dialogue every year in collaboration with a regional partner.<br />\r\n2. &nbsp;Monthly dialogues and events series on selected topics including briefs or working papers.<br />\r\nCollaboration and Partnership<br />\r\n1. &nbsp;Take part in events and dialogues conducted by partner organizations. (20/year)<br />\r\n2. &nbsp;Flagship dialogue every year in collaboration with a regional partner. (1/year)<br />\r\nOthers:<br />\r\n1. &nbsp;Recruitment of and partnership with the best minds.<br />\r\n2. &nbsp;Make a strategy for incorporation of digital technology and data analysis techniques. (One strategy paper)<br />\r\n3. &nbsp;Self-monitoring reports. (Monthly. Listing activities and outputs achieved, with a brief mention of impact, if any.) (12/year for each staff).<br />\r\n<br />\r\n&nbsp;', NULL, NULL, NULL, 2625, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 20:26:17', '2022-12-09 20:26:24'),
(2776, 'PRASHANTI POUDYAL', 'prashanti-poudyal', 'PRASHANTI POUDYAL', NULL, 'Main', 'Team', NULL, 6, 'RESEARCH FELLOW', NULL, '<br />\r\nPrashanti Poudyal is a Research Fellow at the Centre for Social Inclusion and Federalism (CESIF).&nbsp; She completed her Master\'s degree in Development and Governance as a DAAD scholar, from Germany. She has previously worked as a journalist and has authored a script for a romantic comedy series called ‘Maya Bhanne Cheez Yestai Ho’ that aired on AP1 television.&nbsp;', NULL, 2628, NULL, NULL, NULL, '/uploads/banner_image/1670575681_NIK0471_edited-scaled.jpg', NULL, NULL, NULL, NULL, NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 20:30:11', '2023-01-04 16:15:57'),
(2777, 'July-Analysis-Domestic-Politics-and-Governance', 'julyanalysisdomesticpoliticsandgovernance', 'July Analysis: Domestic Politics and Governance', NULL, 'Main', 'Monthly Analysis', NULL, 4, 'The possibilities of realignment in the coalition for the upcoming federal and provincial elections made headlines throughout the month.', NULL, '<p>The possibilities of realignment in the coalition for the upcoming federal and provincial elections made headlines throughout the month. The Nepali Congress has decided to forge an electoral coalition with the member parties of the ruling alliance, while communist parties of the ruling alliance have decided to form a “Socialist Center” and keep both the possibilities intact – an electoral coalition either with the Nepali Congress or with the CPN UML. Earlier in the month, Liu Jianchao, head of the International Liaison Department of the Chinese Communist Party (CCP) visited Nepal and called the communist forces to reconcile their differences and form a single communist block or at least forge an electoral coalition. On the other side, following the controversy for allegedly allowing two unauthorized people to tweak taxes in the budget of the current fiscal year, Finance Minister Janardan Sharma resigned from his post. On July 28, the parliamentary probing committee released a report stating that the allegations against Sharma couldn’t be confirmed.</p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">Timeline of Major Events</span></h2>\r\n&nbsp;\r\n\r\n<table class=\"table table-bordered\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"row\">Date</th>\r\n			<th scope=\"row\">Events</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>July 6</td>\r\n			<td>Finance Minister Janardan Sharma resigned.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>July 10</td>\r\n			<td>Liu Jianchao, the head of the International Liaison Department of the Chinese Communist Party, arrived in Nepal for a four-day visit.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>July 14</td>\r\n			<td>Janata Samajwadi Party (JSP) expelled nine leaders including Federal Council Chairman Baburam Bhattarai.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>July 15</td>\r\n			<td>CPN Maoist Center’s top leader Pushpa Kamal Dahal (Prachanda) went to New Delhi, India for his three-day visit.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>July 18</td>\r\n			<td>Nepali Congress decided to forge an electoral coalition of the ruling alliance for the upcoming provincial and federal elections.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>August 22</td>\r\n			<td>The House of Representatives endorsed an amendment bill to the Citizenship Act, 2006.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>August 31</td>\r\n			<td>The parliamentary probing committee to look into charges levelled against Finance Minister Janardan Sharma released its report.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2><span style=\"color:#FF8C00;\">Early Indications of Parties’ Realignment for Electoral Coalitions</span></h2>\r\n&nbsp;\r\n\r\n<p>On 18 July, the central committee of the Nepali Congress officially decided to form an electoral coalition of the five ruling parties for the upcoming provincial and federal elections. The party also decided to come up with a strategic framework for candidates’ selection and seat sharing in such a way that Nepali Congress’ majority in both the provincial and federal polls could be ensured.</p>\r\n\r\n<p>However, on the other side, the communist parties of the ruling alliance have decided to form a “Socialist Center”. And by doing so, those parties have aimed at exerting leverage collectively over both the Nepali Congress and the CPN UML. The Socialist Center includes CPN Maoist (Center), CPN Unified Socialist, and the Baburam Bhattarai-led faction which is expelled from the Janata Samajwadi Party. As of now, the socialist coalition remains open to the possibility of an electoral collaboration either with the Nepali Congress or with the CPN UML.</p>\r\n\r\n<p>CPN Maoist Center’s leader Pushpa Kamal Dahal Prachanda has frequently claimed that his party intends to forge an electoral coalition with the Nepali Congress. Dahal visited India from 15 to 17 July, and during his visit, he met Nepali Congress’s leader Arju Rana Deuba who also happens to be the first lady, in the presence of Indian leaders. This meeting was met with speculations – that the two reached a consensus in the mediation of Indian leaders on power-sharing after the provincial and federal elections. Dahal has categorically denied this allegation – however, since the communist coalition isn’t in the interest of New Delhi, speculations are rife about the same.</p>\r\n\r\n<p>The other leaders of the proposed “Socialist Center”, on the other hand, have claimed that the coalition with the CPN UML is equally likely. CPN Unified Socialist’s senior leader Jhala Nath Khanal and leaders of the CPN Maoist Center – Narayan Kaji Shrestha and Barsha Man Pun – have even gone on to say that the electoral alliance of the communist forces including the CPN UML is at the top of their priority. And that only if the communist coalition fails to materialize, the Socialist Center would join hands with the Nepali Congress.</p>\r\n\r\n<p>While the CPN Maoist Center benefitted heavily from the electoral coalition of the ruling alliance in the local polls, the Nepali Congress had ripped off the maximum benefits. The rest of the parties, however, couldn’t benefit much owing to their own weak organizational base and shrunk vote bank. The communist coalition, on the other hand, appears attractive to communist parties of the ruling alliance – this is because a communist coalition could smoothly translate into electoral success, unlike the ruling alliance which consists of parties with conflicting ideologies and contrasting voter bases. Even the last time around, in 2017, the CPN Maoist Center had first contested the local polls forging an electoral alliance with the Nepali Congress, but then had switched the camp for the subsequent provincial and federal elections joining hands with the CPN UML. The communist coalition is also in the interest of the CPN UML because the party has undergone multiple splits and has been pushed to the distant second position in the local polls held last May.</p>\r\n\r\n<p>As of now, both the ruling electoral alliance and communist electoral alliance seem equally likely and it is only plausible that the proposed Socialist Centre would eventually join the camp which would ensure the Center with a bigger share in the coalition.</p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">The Head of the International Liaison Department of the Chinese Party, Liu Jianchao Visited Nepal</span></h2>\r\n&nbsp;\r\n\r\n<p>Liu Jianchao, the head of the International Liaison Department of the Chinese Communist Party, visited Nepal on July 10. The four-day visit of the eight-member team marked the second high- profile visit from Beijing within the span of four months after the ratification of the US’s MCC pact by Nepal in February. During this visit, Liu’s team is reported to have urged communist leaders to reconcile their differences and unite once again or at least forge an electoral coalition for the upcoming provincial and federal elections. Even Chinese Foreign Minister Wang Yi in March had made statements along the same lines.</p>\r\n\r\n<p>These attempts/statements of China to push for the communist coalition amount to direct interference in the internal affairs of Nepal and cross all diplomatic lines that a nation is supposed to adhere to. On the other front, those issues that China should have prioritized in these high-profile visits have largely been sidelined. These proclivities hint that Beijing is preoccupied with attempts to reassert its lost influence over Kathmandu, which it enjoyed during the Oli administration, and is occupied with motives of scoring goals against the US-India camp, rather than genuinely seeking to deepen the Kathmandu-Beijing ties.</p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">Finance Minister Janardan Sharma Resigned Amid Controversy</span></h2>\r\n&nbsp;\r\n\r\n<p>On 6 July, Finance Minister Janardan Sharma resigned amidst controversy for allegedly allowing two unauthorized people – a retired senior non-gazette officer and a chartered accountant – to make last-minute changes in taxation for the budget of the fiscal year 2022-23 with a purported motive of benefiting some business groups. A vernacular daily, Annapurna Post, had published a news report on the same – and parliamentarians from across the aisle had sought clarifications with Minister Sharma. The main opposition CPN UML had been demanding the resignation ever since the news broke out.</p>\r\n\r\n<p>On 6 July itself, the House of Representatives formed the Parliamentary Probe Special Committee to look into the matter – the committee consisted of four lawmakers, two each from the Nepali Congress and the Maoist Center, and one each from the CPN (Unified Socialist), Janata Samajwadi Party and Loktrantrik Samajwadi Party. However, the Finance Ministry failed to provide the CCTV footage of May 28, the day prior to the budget announcement, with a claim that only 13 days’ recording was possible to store. The probe committee then reached the conclusion that the CCTV footage of May 28 and 29 was deleted in a premeditated manner. However, on July 28, the probing committee released its final report stating that the charges levelled against Sharma couldn’t be confirmed. Those two lawmakers from the opposition CPN UML in the investigation committee cried foul – and experts argued that there were multiple lapses and flaws during the investigation.</p>\r\n\r\n<p>That the Finance Ministry deleted the CCTV footage of the day in a premeditated manner is telling in itself. On the other hand, PM Deuba failed to take quick actions against the finance minister which raises further doubts – if all this was done in a tacit agreement of the PMO. This isn’t the first time that the crony capitalist nexus has gamed the system to its advantage while eroding institution procedures and the rule of law. In this regard, this entire controversy, on a broader level, is reflective of the extending crony capitalist nexus which has paralyzed the system.</p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">The House of Representatives Endorsed an Amendment Bill to the Citizenship Act, 2006.</span></h2>\r\n&nbsp;\r\n\r\n<p>On 22nd July, the House of Representatives endorsed an amendment bill to the Citizenship Act, 2006 with a majority vote despite objections from the main opposition, CPN UML. The amendment bill has been a matter of controversy ever since the promulgation of the constitution in 2015 – a couple of previous attempts to amend the act had failed. The main bone of contention was about the cooling-off period for foreign women married to a Nepali man. Previously, both CPN UML and the CPN Maoist Center were for a cooling-off period of at least seven years. However, this time around, the CPN Maoist Center backtracked from its position, and then the ruling alliance had enough numbers to amend the bill.</p>\r\n\r\n<p>The parliamentary endorsement of the bill, however, has triggered a debate – while some argue that having no provisions for the cooling-off period would do more harm than good, others rightfully claim that the amendment remains gender-biased as it gives the continuity to preferential treatment for males. Despite this bias, the bill is important in the sense that it has addressed major demands of the Madhes-based parties: allowing citizenship of dissent for children of those who acquired citizenship by birth and naturalized citizenship to foreign women married to men. On the other hand, the CPN UML – a communist party catering to the conservative/ethno-nationalist vote bank – has made attempts to politicize the bill to its electoral advantage. The CPN UML has claimed that this amendment would encourage an influx of foreign females by marrying Nepali males with the motive of gaining Nepali Citizenship – and with this, the party has aimed at consolidating its base amongst the hill-based conservative constituencies.</p>', NULL, 2768, NULL, NULL, 'Sourav Dahal', '/uploads/banner_image/1670575537_Liu-Jianchao-the-head-of-the-International-Liaison-Department-of-the-Chinese-Communist-Party-has-arrived-in-Kathmandu-for-a-visit. (1).jpg', NULL, NULL, NULL, '2022-07', '2022-10-17', NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 20:30:37', '2022-12-11 23:02:46'),
(2778, 'ARPAN GELAL', 'arpan-gelal', 'ARPAN GELAL', NULL, 'Main', 'Team', NULL, 4, 'RESEARCH &amp; PROGRAM COORDINATOR&nbsp;', NULL, 'Arpan Gelal leads Research and Program at the Centre for Social Inclusion and Federalism (CESIF). He completed Erasmus Mundus Joint Masters Degree in International Development Studies (GLODEP) with honours from multiple universities of Czech Republic, France and Italy, fully funded by the European Union. He completed undergraduate degree in Development Studies with KU Vice chancellor Gold Medal and Nepal Bidhya Bhusan Padak. He has worked as a freelance consultant for various development related assignments. He is also a faculty member for Development Studies at National College, Kathmandu University.&nbsp;', NULL, 2628, NULL, NULL, NULL, '/uploads/banner_image/1670575765_1645795258897963-1-scaled-e1662560556976.jpg', NULL, NULL, NULL, NULL, NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 20:34:25', '2023-01-04 16:17:01'),
(2779, 'POOJA GAUTAM', 'pooja-gautam', 'POOJA GAUTAM', NULL, 'Main', 'Team', NULL, 5, 'MONITORING &amp; EVALUATION OFFICER&nbsp;', NULL, 'Pooja Gautam is working as the Monitoring and Evaluation Officer at the Centre for Social Inclusion and Federalism (CESIF). She has comepleted her master’s degree in Business Studies from Tribbhuvan University, Nepal. She is also a lecturer of Business Studies and Finance. She loves to travel to different places.', NULL, 2628, NULL, NULL, NULL, '/uploads/banner_image/1672808909_IMG_20230104_105033.jpg', NULL, NULL, NULL, NULL, NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 20:36:08', '2023-01-04 15:53:29'),
(2782, 'GOMA PRADHAN', 'goma-pradhan', 'GOMA PRADHAN', NULL, 'Main', 'Team', NULL, 13, 'OFFICE ASSISTANT', NULL, 'Goma Pradhan is the Office Assitant at the Centre for Social Inclusion and Federalism (CESIF). She is pursuing her Bachelor\'s degree in Rural Development from Tribhuvan University, Nepal. She supports the administrative and finance department at CESIF.&nbsp;', NULL, 2628, NULL, NULL, NULL, '/uploads/banner_image/1672808108_IMG-20230103-WA0018.jpg', NULL, NULL, NULL, NULL, NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 20:40:31', '2023-01-04 16:26:45'),
(2783, 'SOURAV DAHAL', 'sourav-dahal', 'SOURAV DAHAL', NULL, 'Main', 'Team', NULL, 8, 'RESEARCH FELLOW', NULL, 'Sourav Dahal is a Research Fellow at the Centre for Social Inclusion and Federalism (CESIF). He has a postgraduate diploma in Advanced Study and Research (Political Science and International Relations) from Ashoka University, India. He has previously worked as a writer-analyst, teaching fellow, research assistant and a research intern in different institutions. His research interests include domestic politics of Nepal, international security, geopolitics, democracy and governance among others.', NULL, 2628, NULL, NULL, NULL, '/uploads/banner_image/1670576220_Sourav-Dahal.jpg', NULL, NULL, NULL, NULL, NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 20:42:00', '2023-01-04 16:24:09'),
(2784, 'June-Analysis-Domestic-Politics', 'juneanalysisdomesticpolitics', 'June Analysis: Domestic Politics', NULL, 'Main', 'Monthly Analysis', NULL, 5, 'The Commanding General of the US Army Pacific Charles A. Flynn in his meetings with Prime Minister Sher Bahadur Deuba and Army chief General Prabhu&nbsp;', NULL, '<h2><span style=\"color:#FF8C00;\">Timeline of Major Events</span></h2>\r\n&nbsp;\r\n\r\n<table class=\"table table-bordered\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"row\">Date</th>\r\n			<th scope=\"row\">Events</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>June 4</td>\r\n			<td>CPN Unified Socialist decided to call back its ministers and replace them with the new ones. F</td>\r\n		</tr>\r\n		<tr>\r\n			<td>June 9</td>\r\n			<td>Commanding General of the US Army Pacific Charles A. Flynn arrived in Nepal for his four-day visit.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>June 13</td>\r\n			<td>A national daily accused Finance Minister Sharma of allowing unauthorised people to determine taxes for the budget of the fiscal year 2022-23.J</td>\r\n		</tr>\r\n		<tr>\r\n			<td>June 20</td>\r\n			<td>The government decided not to participate in the State Partnership Program (SPP) of the US</td>\r\n		</tr>\r\n		<tr>\r\n			<td>June 22</td>\r\n			<td>The Judicial Council formed a three-member probe committee to investigate the leaked audio recording between the Kathmandu district judge and the lawyer.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>June 26</td>\r\n			<td>PM Sher Bahadur Deuba reshuffled the cabinet changing ministers representing the CPN Unified Socialist.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2><br />\r\n<span style=\"color:#FF8C00;\">The State Partnership Program (SPP) Controversy</span></h2>\r\n\r\n<p>The Commanding General of the US Army Pacific Charles A. Flynn in his meetings with Prime Minister Sher Bahadur Deuba and Army chief General Prabhu Ram Sharma raised the issue of Nepal’s participation in the United States government’s State Partnership Program (SPP) which sparked off a controversy. Initially, all major parties and the army denied their role in proposing the program to the US – and maintained that no agreements whatsoever had been reached about the same. However, on 16 June, a proposal letter sent by Nepal Army with the government’s authorization to establish the National Guard State Partnership Program was leaked to the media. Then, all involved parties shifted the blame to the others while refusing their involvement in the process. On 20 June, the government decided to not participate in the SPP.</p>\r\n\r\n<p>The State Partnership Program of the US entails security cooperation and exchanges between militaries of the two nations among others – and this provision became the major bone of contention. Although Nepal’s military has been cooperating in various ways with the militaries of other nations, of late, there have been few signs of Nepal being the flashpoint of US-Sino superpower rivalry in the Himalayan region – and in this context, the prospect of military cooperation with the US resulted in the politicisation of the SPP.</p>\r\n\r\n<p>To make the matter worse, both the ruling coalition led by the Nepali Congress and the main opposition CPN Unified Marxist Leninist (UML) sought to gain out of the controversy surrounding the SPP. It is a general practice among mainstream political parties of Nepal to drag issues of foreign affairs into controversy and blame the rival parties for undermining national sovereignty while working under foreign influence. The SPP controversy is no different and is eerily reminiscent of the hotly debated MCC (Millennium Challenge Corporation) pact which had finally ended up with the parliamentary ratification. The opposition CPN UML attempted to capitalise on the SPP controversy and forge into the upcoming elections with an agenda of nationalism branding oneself as the only party which could safeguard the sovereignty of the nation, and resist foreign influence. However, it was later revealed that the government of the CPN UML too was involved in authorising the Nepal Army to request the SPP program. The main party of the ruling alliance Nepali Congress too tried to shift the blame on the CPN UML citing that the proposal letter was sent when CPN UML’s leader K P Sharma Oli was heading the government.</p>\r\n\r\n<p>As in the case of the MCC, all major political parties are equally involved in the SPP, either in proposing the US for the program or in signing the leaked agreement. On June 20, the government then decided to terminate the program and by doing so, put an end to confusion surrounding the SPP. This move of the government was also propelled by criticisms of politicians across the spectrum and experts alike who expressed concerns over the nature of the program and its geopolitical implications.</p>\r\n\r\n<p>However, this SPP saga has once again revealed the duplicity of politicians, their penchant for dragging issues of foreign affairs into controversy for political gains and blame-shifting. These tendencies of politicians when it comes to matters of foreign affairs have made it difficult for our friendly partners in dealing with us, and have called into question the credibility of our political leaders and institutions alike.Early indications of fissures within the ruling coalition</p>\r\n\r\n<p>As of now, both the ruling electoral alliance and communist electoral alliance seem equally likely and it is only plausible that the proposed Socialist Centre would eventually join the camp which would ensure the Center with a bigger share in the coalition.</p>\r\n\r\n<p>A member party of the ruling alliance CPN Unified Socialist has repeatedly blamed other parties of the alliance for not cooperating fully in the local polls held last May which it believes was the major reason behind its dismal electoral performance. Ever since, talks about the possible communist coalition – like the one in the general election of 2017 – have been making some buzz around among the politicians. A faction led by Madhav Kumar Nepal had split from the CPN UML primarily over the chairman K P Sharma Oli, who was then Prime Minister, attempts to dissolve the lower house twice – and the splinter faction formed the CPN Unified Socialist as a new party. The party however couldn’t perform well in the last local poll and won only twenty local units of seven hundred and fifty-three. On the other side, the CPN UML also got pushed to the distant second position in the local polls – and the party split was one among many reasons behind that.</p>\r\n\r\n<p>With this, talks regarding the possible communist coalition have started making rounds as it could be in the interest of both the parties CPN UML and CPN Unified Socialist. The next communist party CPN Maoist Centre, however, benefited heavily from the ruling coalition in the local polls, and as of now, Maoist Centre seems interested in continuing with the ruling coalition itself. The CPN Maoist Centre’s leader has repeatedly asserted that the ruling coalition will forge an electoral alliance even for the forthcoming polls, and the Nepali Congress has attached incentives in continuing with the same coalition. However, there are reports of talks going on between the leaders of the CPN Maoist Center, CPN UML and CPN Unified Socialist for the coalition. A senior communist leader who hasn’t chosen any of the three sides after the split of the Nepal Communist Party (NCP), Bamdev Gautam, has initiated talk with all three communist parties – and other leaders are also said to be involved in finding a common ground for the communist coalition.</p>\r\n\r\n<p>On the other side, there have been some intraparty tensions within the People’s Socialist Party. It is reported that the party is on the verge of a split as two leaders Upendra Yadav and Baburam Bhattarai have failed to reach a consensus on multiple issues of contention. There are also reports of talk between CPN Maoist Centre’s top leader Pushpa Kamal Dahal (Prachanda) and Unified Socialist leader Baburam Bhattarai regarding the formation of a new socialist party. Baburam Bhattarai had left the Maoist party after the promulgation of the constitution in 2015 with an agenda of “alternative politics”, and as his party failed to gain traction, the party had merged with Federal Socialist Forum, Nepal to form Samajwadi Party, Nepal.</p>\r\n\r\n<p>These recent developments herald a likely realigning of parties in the ruling and opposition coalitions. However, it is way too early to definitely predict how those alliances will shape out. Of late, electoral alliances have been a common phenomenon in polls – and how those coalitions get shaped for the forthcoming provincial and federal elections will determine the trajectory of Nepali politics.Finance Minister Janardhan Sharma and Judiciary in a Controversy</p>\r\n\r\n<p>A vernacular daily, Annapurna Post, published a news report alleging Finance Minister Janardhan Sharma of allowing two unauthorised individuals to determine tax rates for the fiscal year 2022-23. It was reported that Sharma instructed four senior finance ministry officials to follow the suggestions of two individuals – a retired senior non-gazetted officer and a chartered accountant – to make last-minute changes in taxation to benefit some business groups. This serious charge against the finance minister got him embroiled in controversy – and lawmakers both from the ruling alliance and opposition sought clarification from the minister in the parliament. Minister Sharma in a press statementrefuted all those allegations as baseless and denied the involvement of those unauthorised individuals.</p>\r\n\r\n<p>PM Deuba and other leaders of the ruling coalition should have set up an investigative committee to look into the charge against the finance minister – and let the truth out to the public. This could only be the responsible course of action by the PM as the charges made by the national daily amount to a serious offence, abuse of authority and dereliction of duty on the part of the finance minister. That the PM chose not to do so, raises further questions – if such a move, if any, was carried out in a tacit agreement of the PM. The controversy has put the credibility and accountability of the government at stake.</p>\r\n\r\n<p>Likewise, a leaked audio recording of the conversation between the judge of the Kathmandu district court and a lawyer has exposed deep-rooted corruption within the judiciary. The recording has the judge and the lawyer discussing a monetary deal to release a businessman, Iccha Raj Tamang, who is jailed for embezzlement of deposits at the cooperatives. The two are also heard discussing the involvement of the chief justice, and his green signal for the monetary deal between the two. On June 22, a three-member committee was formed to investigate the audio recording by the Judicial Council.</p>\r\n\r\n<p>The extending influence of big corporations and business houses over the political leaders and organs of the government has been one major problem for decades; and these two sagas – in the executive and the judiciary – have further brought into light the crony-capitalist nexus which have paralyzed the institutions of the state.</p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">PM Deuba reshuffled the cabinet</span></h2>\r\n&nbsp;\r\n\r\n<p>The CPN Unified Socialist decided to recall its ministers and replace them with the new ones on June 4th but it took almost entirely a month to bring this into effect. The decision to replace the ministers representing the party with the ones ignited tensions within the CPN Unified Socialist, and some lawmakers even threatened to revolt against the party leadership for the same. However, on June 26, ministers representing the party resigned, and PM Deuba appointed the new ones. With the reshuffle, Metmani Chaudhary has been appointed as minister for Urban Development, Jeevan Ram Shrestha as minister for Tourism, Culture and Civil Aviation, Sher Bahadur Kunwar as minister for Labour, Employment and Social Security, Bhawani Khapung as minister for Health and Population and Hira Chandra KC as minister of state for Health and Population.</p>', NULL, 2768, NULL, NULL, 'Sourav Dahal', '/uploads/banner_image/1670576237_ROS_KTMimage_6487327.jpg', NULL, NULL, NULL, '2022-06', '2022-07-12', NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 20:42:17', '2022-12-09 20:52:27'),
(2788, 'PRAKRITI ADHIKARY', 'prakriti-adhikary', 'PRAKRITI SHREE ADHIKARY', NULL, 'Main', 'Team', NULL, 7, 'RESEARCH FELLOW', NULL, 'Prakriti Shree Adhikary is a Research Fellow at the Centre for Social Inclusion and Federalism (CESIF). She completed her MA specialization in International Relations in 2017 from Jawaharlal Nehru University, India. Prior to CESIF, Prakriti worked in Transparency International Nepal and has worked as a freelance researcher in several development projects.&nbsp;Her interest includes national security, gender and social inclusion, international politics among others.&nbsp;', NULL, 2628, NULL, NULL, NULL, '/uploads/banner_image/1672822353_IMG_20230104_143323.jpg', NULL, NULL, NULL, NULL, NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 20:48:05', '2023-01-04 19:39:21');
INSERT INTO `navigations` (`id`, `nav_name`, `alias`, `caption`, `caption_nepali`, `nav_category`, `page_type`, `page_template`, `position`, `short_content`, `short_content_nepali`, `long_content`, `long_content_nepali`, `parent_page_id`, `icon_image`, `featured_image`, `icon_image_caption`, `banner_image`, `link`, `main_attachment`, `attachment`, `page_title`, `page_keyword`, `page_description`, `page_status`, `nav_status`, `extra_one`, `extra_two`, `extra_three`, `created_at`, `updated_at`) VALUES
(2789, 'May-Analysis-Domestic-Politics-and-Governance', 'mayanalysisdomesticpoliticsandgovernance', 'May Analysis: Domestic Politics and Governance', NULL, 'Main', 'Monthly Analysis', NULL, 6, 'The second-ever local poll since Nepal’s transition into a federal republic with three tiers of government was held on May 13<sup>th</sup>.&nbsp;', NULL, '<h2><span style=\"color:#FF8C00;\">Timeline of Major Events</span></h2>\r\n&nbsp;\r\n\r\n<table class=\"table table-bordered\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"row\">Date</th>\r\n			<th scope=\"row\">Events</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>May 13</td>\r\n			<td>Local polls were held across the country.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>May 16</td>\r\n			<td>Indian Prime Minister Narendra Modi visited Lumbini.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>May 17</td>\r\n			<td>The budget session of the parliament convened.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>May 20</td>\r\n			<td>Pre-budget discussion of the parliament ended.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>May 29</td>\r\n			<td>Finance Minister Janardan Sharma presented the annual budget.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2>Local polls held throughout the country</h2>\r\n\r\n<p>The second-ever local poll since Nepal’s transition into a federal republic with three tiers of government was held on May 13th. About sixty-three percent of 17,733,723 registered voters participated in the election to elect 35,221 representatives for 753 local units.As many as seventy-nine political parties and 145,011 contestants competed for various posts, out of which three hundred ninety were elected unopposed.Although there were a few occurrences of violent incidents. The local poll was, in general, free, fair and peaceful across the country. However, owing to logistical challenges, vote counting was sluggish, and the results kept trickling in for the entire second half of the month.</p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">Electoral Coalition paid off for the ruling alliance</span></h2>\r\n&nbsp;\r\n\r\n<p>The ruling five-party alliance had forged an electoral alliance for polls in almost one-third of the 753 local units. However, owing to interparty and intraparty disputes about coalition and selection of the consensus candidates, only in twelve of seventy-seven districts, did a full-fledged alliance of the ruling alliance materialise. Despite this, almost all parties of the ruling alliance benefitted from the coalition. The Nepali Congress emerged victorious, winning three hundred and twenty-nine local units. The electoral alliance enabled the NC to gain an edge against its main rival the CPN UML which is now pushed to the distant second position with victories in only two hundred and five local units. Even in the last local election of 2017, the Nepali Congress and the CPN Maoist had forged an election coalition but the CPN UML had still managed to win the largest number of local units. However, this time around, the Nepali Congress has a lead of more than one hundred local units against the CPN UML – and in more than one-third of the local units that Nepali Congress won, it had the backing of other parties in the ruling alliance.</p>\r\n\r\n<p>Likewise, the CPN Maoist Centre which had been in a steady decline over the years emerged as a formidable political force winning as many as one hundred and twenty-one local units. In more than half of the seats that the CPN Maoist Center won, it was backed by the Nepali Congress and/or other parties of the ruling alliance. The next party of the ruling alliance, the Janata Samajbadi Party (JSP), won thirty local units. Although the JSP couldn’t perform at par with the Nepali Congress or the CPN Maoist Center; and performed below expectations even in Madhes – a region where it has a strong foothold – the electoral alliance was still instrumental for the JSP in securing victories in those thirty local units. Likewise, the CPN Unified Socialist – a new party formed by splitting from the CPN UML – won only twenty local units and couldn’t benefit much from the alliance owing to its weak political and organisational base. The leader of the party Madhav Kumar Nepal then accused that the dismal electoral performance had to do with some parties of the ruling alliance not cooperating fully with his party.</p>\r\n\r\n<p>On the other side, the CPN UML which had forged an election coalition with some fringe parties performed poorly compared to the local election of 2017. The last time, CPN UML had won two hundred and ninety-four units which got reduced to two hundred and five units in this poll. CPN UML’s performance indicates that a party can’t rely solely on the popularity of a leader – in this case, K P Sharma Oli – to pull off an electoral success. The result also heralds further difficulties for the CPN UML, now that the ruling alliance is more likely to continue with an electoral alliance in both the upcoming provincial and federal elections.</p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">CPN UML resorted to the “rigged election” narrative</span></h2>\r\n&nbsp;\r\n\r\n<p>Once results started trickling in, and the early trend of the CPN UML performing poorly compared to the Nepali Congress started to emerge, the CPN UML accused the ruling alliance of rigging the election. The UML, on May 14, issued a statement and alleged that the “ruling alliance mobilised bureaucrats, and security personnel in rigging before, during and after the election.” The UML highlighted fifteen different points to support the claim but the fact that the UML chose to wait for the early trend of the election to accuse of rigging “before, during and after the election” is telling in itself. Those accusations don’t hold any ground because it is apparent that the CPN UML chose a convenient narrative of a “rigged election” to justify its poor performance rather than going into the introspection of its shortcomings – the unconstitutional moves of dissolving the lower house twice which had led to the year-long political and constitutional crisis, and its failure to deliver on the front of good governance. And the CPN UML hasn’t even undertaken any legal proceedings for the same. As a party which has led the government a couple of times over the years, the CPN UML should have accepted the defeat in grace as a natural outcome of an electoral contest. However, that it chose not to do so raises questions over the party’s commitment to democratic values and ideals. The communist parties of Nepal, more often than not, have taken refuge in this narrative following poor electoral performances – and such narratives have only added to further corrode the trust of the public in democratic institutions and democratic processes.</p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">Discontents against the mainstream political parties in a few urban centers</span></h2>\r\n&nbsp;\r\n\r\n<p>In Kathmandu metropolitan city, an independent candidate Balendra Shah (Balen) won the mayoral contest. Similarly, independent candidates Harka Raj Sampang Rai and Gopal Hamal won the post of mayor in Dharan and Dhangadi sub-metropolitan cities respectively. These independent candidates, with no organisational structure and voter base to rely on, outperforming well-established political parties is a new trend that has emerged this election. This is the first time, after the reintroduction of democracy in 1990 that independent candidates have won in major cities. Further, as many as three hundred and eighty-five independent candidates won for different posts across the country. These results are reflective of the discontent of constituents particularly that of the urban educated middle class, against mainstream political parties. Those parties have failed to deliver good governance over the years which has resulted in disillusionment amongst a section of the population. As of now, this trend is confined to a few urban areas but should the mainstream political parties fail to deliver yet again, space for alternative politics is likely to grow in the days to come.</p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">Issues of local units out of focus in the local polls</span></h2>\r\n&nbsp;\r\n\r\n<p>The three tiers of government are envisioned to effectively decentralise the state structure – and therefore during the local polls, issues concerning the effective implementation of the three tiers of government and problems of local bodies should have been in the focus. Likewise, policies about local bodies, issues of corruption and development should have been the factors shaping the results of the local polls. But local polls too were reduced to a mere number and strategy games by the political parties. For the last two months, prospects of coalitions, and inter party, intraparty disputes made the headlines while issues of local bodies were almost completely sidelined. When reduced to mere number games, elections in essence become perfunctory and don’t serve their true purpose. This, however, has been continuing for years ever since the practice of multiparty democracy, and has become one major challenge in Nepal’s transition to democratic maturity.</p>\r\n\r\n<h2><span style=\"color:#FF8C00;\">The budget session of the parliament convened</span></h2>\r\n&nbsp;\r\n\r\n<p>On May 17, the budget session of the parliament convened. The main opposition UML decided to end the obstruction of the parliament after eight long months and allowed the house to function properly. Since last September, the opposition UML had been obstructing the house over the speaker’s refusal to confirm the expulsion of 14 lawmakers of the CPN Unified Socialist – a splinter party of the CPN UML. Through continued obstruction of the house for months, the CPN UML aimed at justifying ex-PM Oli’s unconstitutional move of dissolving the lower house twice by rendering the house dysfunctional. As a consequence, in those eight months, the house had only managed to pass the budgetary bills of the last fiscal year and ratify the MCC compact. Finally, this time around, the CPN UML corrected its course and decided to end the longest ever obstruction in Nepal’s parliamentary history. The poor performance in the local polls could have played part in UML’s decision to let the house function because, unlike the CPN UML’s expectations, the party’s decision to defend unconstitutional moves of dissolving the lower house twice ultimately proved costly even in the polls. With the CPN UML ending its months-long obstruction, the budget session of the parliament ran smoothly and held deliberations over the upcoming budget. On May 20, the house concluded its pre-budget discussion, and on May 29th, the fi</p>', NULL, 2768, NULL, NULL, 'Sourav Dahal', '/uploads/banner_image/1670576748_Democracy-Governance.-img.jpg', NULL, NULL, NULL, '2022-05', '2022-07-07', NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 20:50:48', '2022-12-09 20:53:39'),
(2792, 'Proceedings-Reports', 'proceedingsreports', 'Proceedings Reports', NULL, 'Main', 'Group', NULL, 4, NULL, NULL, NULL, NULL, 2615, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 20:57:24', '2022-12-09 20:57:24'),
(2793, 'Chinese-Border-Encroachment-in-Humla', 'chineseborderencroachmentinhumla', 'Chinese Border Encroachment in Humla', NULL, 'Main', 'Proceeding Report', NULL, 1, 'As a small landlocked country sandwiched between two giants—India and China—Nepal’s sensitivity for territorial integrity and sovereignty stands ou', NULL, '<p>As a small landlocked country sandwiched between two giants—India and China—Nepal’s sensitivity for territorial integrity and sovereignty stands out as particularly strong. Never formally colonized, Nepali people appear extra possessive towards their country’s natural heritages and historical richness and have a strong emotional attachment. Therefore, any sort of attack on Mt. Everest and Gautam Buddha, for instance, quickly flares up people’s sentiments and aggression. Given these dynamics, India’s perceived bossiness and occasional border conflicts often invite a huge national outburst. This was seen in the most recent India-Nepal border conflict in the Kalapani region, where all the political parties came forward to support the government’s unilateral action of publishing a new map and amending the constitution.[1] However, the government’s reluctance to confront the northern neighbor despite repeated complaints of encroachment begs the question of the ruling party’s true attitude and the intention.</p>\r\n\r\n<h6>Source of the Conflict</h6>\r\n\r\n<p>The issue of border encroachment by China in Humla surfaced when locals of Namkha Rural Municipality reported to the District Administration Office of nine new Chinese construction in Nepali land between the Pillars 11 and 12. The chair of Namkha, Bishnu Bahadur Tamang, claimed that Nepal’s territory “extends two kilometers north from where the buildings are.”[2] Responding to the locals, the assistant Chief District Officer (CDO), Dattaraj Hamal, made an initial visit to the area. Later, the Home Ministry sent a team of government officials, security personnel, and local representatives—led by Humla’s CDO Chiranjibi Giri—to inspect the situation and report to the government. The initial problem seemed to be the missing Pillar no. 11, which was “damaged during a road construction on the Nepali territory some 12 years back.”[3] Locals believed that drawing a straight line between Pillars 11 and 12 would easily show that the new Chinese constructions were in the Nepali territory.</p>\r\n\r\n<p>Reportedly, the Chinese officials at the border had denied the possibility of any talks and had stressed that the constructions were on Chinese land. Even the spokesperson for the Chinese Embassy, Zang Si, falsified the ‘accusations’ of Chinese encroachment and instead accused Indian media of sensationalizing the issue. Interestingly, the border inspection team found the missing Pillar 11 in Ward No. 6 of the rural municipality in Takule,[4] and the Ministry of Foreign Affairs was quick to issue a statement confirming that “the said buildings are not located within the Nepali territory.”[5] Referring to an inter-ministerial field inspection in 2016 on the same issue, the ministry clarified that the team had concluded the said buildings to be “located one kilometer inside the Chinese territory from the Nepal-India border.”[6] According to the government, Nepal did not have a border conflict with China.</p>\r\n\r\n<h6>Field Investigation by Jiwan Bahadur Shahi</h6>\r\n\r\n<p>Nepali Congress (NC)’s Central Committee member and Provincial Lawmaker, Jiwan Bahadur Shahi, was cynical of the government’s rushed conclusion. Therefore, Shahi, who is also a resident of Humla, led his own team to Humla for further investigation. After an 11-day study from Pillar 5 to Pillar 12,[7] Shahi’s team came up with some crucial findings, which led the opposition Nepali Congress to conclude that China has, indeed, encroached on Nepali land in Humla.[8] Shahi’s team concluded the following[9]:</p>\r\n\r\n<h3>1. Chinese constructions in Lolungjong near Pillar no. 12 are in Nepali territory.</h3>\r\n\r\n<p>The investigation team concluded that the new Chinese buildings in Lolungjong are in Nepali territory and that Pillar no. 12 lies to the north of the constructions. In addition, local residents claim that they went to Lolungjong for livestock farming and cattle grazing. The hard evidence of the pillar’s location in reference to the Chinese buildings and local’s account of their lifestyle in the region suggest at least a possibility of encroachment, which requires further bilateral investigation.</p>\r\n\r\n<p>In addition, Shahi’s team found that the Pillar no. 12 was newly constructed. The apparent changed-orientation of the pillar, new fonts on it, and scattered remains of the old pillar indicated that the Chinese side had unilaterally built the new pillar, without Nepal government’s knowledge. If so, it violates the international as well as bilateral protocols of border management—an agreement Nepal and China signed in 1960.</p>\r\n\r\n<p>As Fig 2 below shows, although the printed date of 2018 B.S. (1962) is supposed to indicate the year the pillar was built, the color, font, and its overall status clearly indicate that it was built recently. If the government of Nepal has no knowledge of it, it is crucial to investigate what happened with the old pillar and who built the new one. Why was it done unilaterally?</p>\r\n\r\n<h6>China has encroached Nepali land in Kith, Hilsa</h6>\r\n\r\n<p>Shahi’s team found that “China has also encroached Nepali territory near the border pillars 5(1) and 5(2) in the Kith area, and removed the old Junge Pillar no 5(2).”[11] The report also states that Nepalese are restricted from entering and using their ‘registered’ land in Kith. Even security personnel are restricted to inspect the pillar.</p>\r\n\r\n<h6>China has constructed fences in the no-man’s-land of Hilsa</h6>\r\n\r\n<p>The Shahi-led investigation found China to have violated the international obligation “by constructing a gate in between the Pillar 9(2) in no man’s land at the Hilsa border area.”[12] Again, this unilateral act requires Nepal government’s attention and China’s clarification on why the gate has been constructed in the no-man’s-land.</p>\r\n\r\n<h6>No encroachment around Pillars 10 and 11</h6>\r\n\r\n<p>Finally, Shahi’s team found that there was no encroachment around the Pillars 10 and 11.</p>\r\n\r\n<h6>Discussion and Conclusion</h6>\r\n\r\n<p>China has continued to play with Nepali people’s sentiments, albeit subtly. Be it the unilateral measurement of Mt. Everest or the occasional claims over it, China has had an eagle eye on Nepal’s northern frontier and its glorious mountains. If it were not for its volatile Tibet region, which requires Nepal’s commitment to the one-China policy, China would most certainly have a bossier attitude towards Nepal and its northern territory. Nevertheless, China has occasionally projected its expansionist attitude to Nepal. Sadly, Nepal’s ruling communist party has remained worryingly silent on such occasions. The border encroachment in Humla is one such example, where the ruling NCP government rushed to falsify the locals’ claims instead of initiating a joint investigation and questioning the Chinese moves. As former Foreign Minister and NC leader Dr. Prakash Sharan Mahat dubs it, the NCP government’s silence on this issue is “irresponsible.”[13]</p>\r\n\r\n<h2>A Time-line of major events</h2>\r\n\r\n<table class=\"table table-bordered\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"row\">Year</th>\r\n			<th scope=\"row\">Event</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>1955</td>\r\n			<td>Nepal-China diplomatic relations formally begin</td>\r\n		</tr>\r\n		<tr>\r\n			<td>20 April 1960</td>\r\n			<td>Anti-China protests led by NC</td>\r\n		</tr>\r\n		<tr>\r\n			<td>21 March 1960</td>\r\n			<td>Border agreement between China and Nepal</td>\r\n		</tr>\r\n		<tr>\r\n			<td>1960</td>\r\n			<td>Nepal China Joint Boundary Commission formed</td>\r\n		</tr>\r\n		<tr>\r\n			<td>5 October 1961</td>\r\n			<td>Nepal China Boundary Treaty signed by King Mahendra</td>\r\n		</tr>\r\n		<tr>\r\n			<td>1979</td>\r\n			<td>First Joint Border Inspection</td>\r\n		</tr>\r\n		<tr>\r\n			<td>1989</td>\r\n			<td>Second Joint Border Inspection</td>\r\n		</tr>\r\n		<tr>\r\n			<td>2006</td>\r\n			<td>Third Joint Border Inspection</td>\r\n		</tr>\r\n		<tr>\r\n			<td>2016</td>\r\n			<td>Locals of the Lapcha-Lolung region demand border supervision for the first time after Chinese constructions were built between pillars 11 and 12. The government of Nepal concluded that the buildings were in Chinese land</td>\r\n		</tr>\r\n		<tr>\r\n			<td>2020</td>\r\n			<td>Locals request re-inspection of the Chinese-occupied area between Pillars 11 and 12; Home Ministry-dispatched team concludes no border dispute</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Jiwan Bahadur Shahi’s investigation offers a crucial insight into what the government seems to have missed in its superficial border inspection. Based on the report, the opposition Nepali Congress has officially concluded that China has indeed encroached on Nepali land in Humla. However, the NCP government seems totally uninterested in antagonizing its communist neighbor in the north by questioning any of its moves. Instead, the NCP appears more concerned with maintaining its relationship with the Chinese Communist Party (CCP) by comfortably overlooking Nepal’s national interest.</p>\r\n\r\n<p>As the above timeline shows, there has been no joint border inspection since 2006. While the local residents are repeatedly claiming that China has encroached on Nepali land and when the opposition Nepali Congress already stands behind the locals with concrete proofs, the ruling party should show seriousness for the border issue in the north and work for the national interest of the country. It is about time Nepal’s government pressed China for a joint border investigation</p>\r\n\r\n<ol>\r\n	<li>[1]&nbsp;<a href=\"https://kathmandupost.com/columns/2020/06/04/limpiyadhura-kalapani-lipulekh-dispute\"><span style=\"color:#FF0000;\">https://kathmandupost.com/columns/2020/06/04/limpiyadhura-kalapani-lipulekh-dispute</span></a></li>\r\n	<li><span style=\"font-size:16px;\">[2]<a href=\"http://https://kathmandupost.com/national/2020/09/23/a-missing-border-pillar-in-humla-creates-row-between-nepal-and-china#:~:text=Nepal%20and%20China%20are%20engaged,has%20now%20constructed%20some%20buildings\"><span style=\"color:#FF0000;\">http://https://kathmandupost.com/national/2020/09/23/a-missing-border-pillar-in-humla-creates-row-between-nepal-and-china#:~:text=Nepal%20and%20China%20are%20engaged,has%20now%20constructed%20some%20buildings</span></a></span></li>\r\n	<li><span style=\"font-size:16px;\">[3]<a href=\"https://kathmandupost.com/national/2020/09/23/a-missing-border-pillar-in-humla-creates-row-between-nepal-and-china#:~:text=Nepal%20and%20China%20are%20engaged,has%20now%20constructed%20some%20buildings\"><span style=\"color:#FF0000;\">https://kathmandupost.com/national/2020/09/23/a-missing-border-pillar-in-humla-creates-row-between-nepal-and-china#:~:text=Nepal%20and%20China%20are%20engaged,has%20now%20constructed%20some%20buildings</span></a></span></li>\r\n	<li>[4]<a href=\"https://kathmandupost.com/national/2020/09/24/pillar-number-11-along-nepal-china-border-in-humla-which-was-said-to-be-missing-has-been-found\"><span style=\"color:#FF0000;\">https://kathmandupost.com/national/2020/09/24/pillar-number-11-along-nepal-china-border-in-humla-which-was-said-to-be-missing-has-been-found</span></a></li>\r\n	<li>[5]<a href=\"https://mofa.gov.np/press-statement-on-news-report-on-nepal-china-border/\"><span style=\"color:#FF0000;\">https://mofa.gov.np/press-statement-on-news-report-on-nepal-china-border/</span></a></li>\r\n	<li>[6]<a href=\"https://mofa.gov.np/press-statement-on-news-report-on-nepal-china-border/\"><span style=\"color:#FF0000;\">https://mofa.gov.np/press-statement-on-news-report-on-nepal-china-border/</span></a></li>\r\n	<li>[7]<a href=\"https://english.khabarhub.com/2020/12/134763/?fbclid=IwAR0wf5nMaa4wneyanOD53rqIDOg-AJ-XD8AA_Dc1815HbVJAFqjhq3wh9WU\"><span style=\"color:#FF0000;\">https://english.khabarhub.com/2020/12/134763/?fbclid=IwAR0wf5nMaa4wneyanOD53rqIDOg-AJ-XD8AA_Dc1815HbVJAFqjhq3wh9WU</span></a></li>\r\n	<li>[8]<a href=\"http://https://english.kathmandupati.com/nepal/3035/\"><span style=\"color:#FF0000;\">http://https://english.kathmandupati.com/nepal/3035/</span></a></li>\r\n	<li>[9]<a href=\"https://english.khabarhub.com/2020/12/134763/?fbclid=IwAR0wf5nMaa4wneyanOD53rqIDOg-AJ-XD8AA_Dc1815HbVJAFqjhq3wh9WU\"><span style=\"color:#FF0000;\">https://english.khabarhub.com/2020/12/134763/?fbclid=IwAR0wf5nMaa4wneyanOD53rqIDOg-AJ-XD8AA_Dc1815HbVJAFqjhq3wh9WU</span></a></li>\r\n	<li>[10]<a href=\"https://english.khabarhub.com/2020/12/134763/?fbclid=IwAR0wf5nMaa4wneyanOD53rqIDOg-AJ-XD8AA_Dc1815HbVJAFqjhq3wh9WU\"><span style=\"color:#FF0000;\">https://english.khabarhub.com/2020/12/134763/?fbclid=IwAR0wf5nMaa4wneyanOD53rqIDOg-AJ-XD8AA_Dc1815HbVJAFqjhq3wh9WU</span></a></li>\r\n	<li>[11]<a href=\"https://english.khabarhub.com/2020/15/134980/\"><span style=\"color:#FF0000;\">https://english.khabarhub.com/2020/15/134980/</span></a></li>\r\n	<li>[12]<a href=\"https://english.khabarhub.com/2020/15/134980/\"><span style=\"color:#FF0000;\">https://english.khabarhub.com/2020/15/134980/</span></a></li>\r\n</ol>', NULL, 2792, NULL, NULL, 'CESIF Nepal', '/uploads/banner_image/1671360372_default-img.jpg', NULL, NULL, NULL, '2022-09', '2022-09-05', NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 21:15:15', '2022-12-18 22:31:12'),
(2794, 'News-Digest-(July-02–July-08-2020)', 'newsdigestjuly02july082020', 'News Digest (July 02– July 08, 2020)', NULL, 'Main', 'Proceeding Report', NULL, 2, '<em>Nepal Communist Party (NCP) dispute &amp; the Standing Committee Meeting:&nbsp;</em>After the President prorogated the parliamentary sessions recommended by&nbsp;', NULL, '<h6>Domestic Politics</h6>\r\n\r\n<p>Nepal Communist Party (NCP) dispute &amp; the Standing Committee Meeting: After the President prorogated the parliamentary sessions recommended by the cabinet, amidst an ongoing standing committee of the ruling NCP, the intra-party dispute within NCP has climaxed to a peak, with Dahal-Nepal faction demanding Oli’s resignation as both party chair and PM while Oli adamant on not quitting either. PM Oli is trying to misuse the president’s power, accuses NCP leader Jhalanath Khanal.[1]</p>\r\n\r\n<p>Interpreting that Oli is trying to split the party, the party co-chair Dahal suspended the standing committee meeting until July 4 to allow some time to try and forge an agreement with Oli and prevent the party from splitting. The day-long exercise on July 3 ended without any progress.[2] After briefing the meeting to other secretariat members, Dahal-Nepal alliance concluded that they will resume the meeting and move ahead with the secretariat’s majority if no agreement us made by 11 am on July 4.</p>\r\n\r\n<p>Prime minister Oli has been extending his arms for support from NCP’s sister organizations; meeting student and youth leaders in July 2, 2020, Oli asked them to create a ‘favorable environment’ for him, amidst the growing pressure on him.[3] Again, the standing committee meeting scheduled for July 4 was postponed till July 6, as the leaders failed to draw an agreement.[4]</p>\r\n\r\n<p>After the July 5 meeting between Oli and Dahal failed to reach an agreement, another ‘decisive’ meeting between the two was to happen on July 6, before the standing committee meeting kicked off. The standing committee meeting is likely to get further postponed if the leaders fail to reach an agreement.[5]</p>\r\n\r\n<p>The standing committee meeting has been postponed yet again, until July 8, 2020. The decision was made to buy more time to try and reach an agreement and avoid party split.[6] The standing committee meeting scheduled for July 8 was postponed for the fourth time, until July 10, 2020.[7]</p>\r\n\r\n<p>In a meeting with President Bidya Devi Bhandari, NCP’s senior leaders clarified that there have been no discussions in the party about impeaching the President—something Prime Minister Oli accused the Nepal-Dahal faction of conspiring along with his resignation.[8] NCP co-chair Pushpa Kamal Dahal and NCP leaders Jhalanath Khanal, and Madhav Nepal met the President at Sital Niwas on July 5, 2020.[9]</p>\r\n\r\n<p>Amidst NCP’s growing internal crisis, Chinese Ambassador to Nepal Hou Yanqi met NCP’s senior leader and chief of its foreign affairs Madhav Kumar Nepal late night on July 5.[10] Ambassador Hou had also held a meeting with president Bhandari on the same day.[11]</p>\r\n\r\n<p>Millennium Challenge Corporation: MCC’s future seems uncertain, as the parliamentary session comes to an unexpected halt following NCP’s internal struggle. The compact was supposed to be ratified by the federal parliament within the current session.[12]</p>\r\n\r\n<p>Janata Samajwadi Party (JSP) &amp; Sarita Giri: On July 7, the Janata Samajwadi Party co-chairs Mahantha Thakur and Upendra Yadav, along with leaders Dr. Baburam Bhattarai and Rajendra Mahato met the NC president Sher Bahadur Deuba at his residence in Budhanilkantha.[13]</p>\r\n\r\n<p>The JSP taskforce—led by party chief secretary Ram Sahaya Prasad Yadav—recommended to remove Sarita Giri from the party and, therefore, the parliament—for going against the party whip to vote for constitutional amendment.[14] The party decided to take away even her general membership, which automatically strips her membership in the parliament according to the Political Party Act of 2016.[15]</p>\r\n\r\n<p>Giri claimed that the party’s decision to oust her is unconstitutional and that she will go to the court to challenge the decision.[16] On the same account, the National Human Rights Commission member Mohana Ansari stated that JSP’s action against Sarita Giri violates freedom of expression.[17] The JSP is set to send Lila Sitaula to replace Sarita Giri in the parliament.[18]</p>\r\n\r\n<p>Nepali Congress: Nepali Congress expressed its displeasure with the government’s unilateral decision to end the parliamentary sessions.[19]</p>\r\n\r\n<p>NCP leader Subas Chandra Nembang met NC president Sher Bahadur Deuba and NC leader Minendra Rijal at Deuba’s residence in Budhanilkantha. Although the meeting was said to be regarding appointments in the constitutional bodies, many speculate it to be Oli’s ploy to lure Deuba for a possible alliance if the NCP splits.[20]</p>\r\n\r\n<p>On July 6, the Central Working Committee of the opposition Nepali Congress decided to hold the party’s 14th national convention in Kathmandu from February 19 to 22, 2021.[21]</p>\r\n\r\n<p>Governance</p>\r\n\r\n<p>Government urged to guarantee citizen’s health right: The National Human Rights Commission has urged the Government of Nepal to guarantee the right to health to every citizen amid the spike in COVID-19 cases and fatalities in the country. Issuing a press release on July 5, the rights body expressed grave concern regarding the finding of traces of SARS-Cov-2 in drainage water of Kathmandu valley during laboratory test of samples and said it could be an indication that the urban area had already experienced community transmission of COVID-19 as opined by researchers.[22]</p>\r\n\r\n<p>Increase PCR tests – NHRC to the government: The National Human Rights Commission has urged the government to increase the number of PCR tests to identify more vulnerable areas and people. They asked the government to ensure additional measures to prevent and control the spread of Coronavirus. It has also expressed concern about the condition of human rights during the COVID-19 crisis across the country, and urged the government to enhance the quality of quarantines in the country.[23]</p>\r\n\r\n<p>Government issues higher education facilitation framework: The Ministry of Education, Science and Technology has approved the ‘Alternative learning in higher education facilitation framework, 2077’ and introduced the framework for implementation through the University Grants Commission. The framework has made arrangements for an alternative process regarding teaching, learning, research, examination and evaluation at university-level higher educational institutions. The Cabinet meeting held on July 3 took the decision, and the bodies concerned have been urged to follow it.[24]</p>\r\n\r\n<p>Sarita Giri to be recalled as Member of Parliament: Samajbadi Party decided to recall leader Sarita Giri as lawmaker and party member for defying the party’s decision to unanimously endorse the constitutional amendment to revise the national map of the country. A three-member panel led by party general secretary Ram Sahay Prasad Yadav recommended that the party sacked Giri from both as a lawmaker and the party member, and the meeting of the officer bearers of the party took the decision accordingly on July 7, said Bishwodip Pandey, deputy head of the party’s Publicity Department. Giri had registered an amendment to the constitutional amendment bill registered by the government in Parliament on May 22.[25]</p>\r\n\r\n<p>Bill proposes cross country power trade via private companies: The government proposed letting private companies trade electricity with parties outside the country. The government recently registered a bill to amend the existing electricity law in the National Assembly. The bill proposes that private companies interested in trading and exporting electricity have to acquire a license from the Ministry of Energy, Water Resources, and Irrigation. Provincial and local level governments will also be allowed to issue such licenses for small-scale projects.[26]</p>\r\n\r\n<h6>International Relations &amp; Foreign Policy</h6>\r\n\r\n<p>Japan grants MUSD 300 to Nepal: Nepal accepted a grant worth MUSD 300 from the government of Japan. The funds have been processed to the school sector development program.[27]</p>\r\n\r\n<p>Renewal of temporary quotas: The Cabinet meeting on June 29 decided to renew the temporary quotas of ‘joint secretary and undersecretary for Nepal’s Permanent Mission to the United States, Geneva for the fiscal year, 2076/77’.[28]</p>\r\n\r\n<p>Chinese Ambassador meddles once again: Chinese Ambassador to Nepal Hou Yanqi was once again spotted talking to ruling party leaders amid increasing intra-party tensions. She is scrambling to keep the party’s unity intact and held a conversation with leader Madhav Kumar Nepal on July 5.[29] These meetings with high officials at the time of political unrest have drawn criticism. Foreign Ministry also revealed that the President’s office had violated the Foreign Ministry’s code of conduct.[30]</p>\r\n\r\n<p>Oli government’s foreign policy condemned: Following the deteriorating relations with India and other external disputes, experts and opposition party have termed Oli’s foreign policy as ‘disarray’ and ‘disoriented’. Nepali Congress and the Janata Samajbadi Party on July 7 concluded that Oli government’s foreign policy is decreasing Nepal’s credibility in the international sphere. The oppositions opined that the ruling NCP’s policy is a double standard and is tilting towards one neighbour due to which India-Nepal relations have reached a new low. The tensions with India have left everyone worried.[31]</p>\r\n\r\n<h6>Security</h6>\r\n\r\n<p>Nepal’s Covid-19 tally crosses 16,000 with 35 recorded deaths: A 45-year-old male from Ramaroshan Rural Municipality-5 in Achham district of far-western Nepal tested positive for COVID-19 after his death.[32] An 85-year-old man from New Baneshwor in Kathmandu who had tested positive for the virus on June 30 passed away on July 1.[33] A 44-year-old male of Palungtar Municipality-7, Gorkha, died during the course of treatment on July 4.[34] A 75 year-old man, a resident of Devdaha Municipality passed away while undergoing treatment on July 1.[35] A 29-year-old man from Nisikhola Rural Municipality ward 3 in Baglung succumbed to the virus on July 5.[36] All these deaths added up to the mortality of COVID-19 to 35.</p>\r\n\r\n<p>According to the Ministry of Health, so far, 255,728 polymerase chain reaction tests have been conducted across the country. The ministry also said a total of 6,811 individuals have recovered from the virus.[37]</p>\r\n\r\n<p>Seven crew members of Nepal Airlines test positive for COVID-19: Nepal Airlines confirmed that seven flying crew members including three pilots have tested positive for COVID-19 on July 6. According to the spokesperson for the airlines, the seven members have been placed in isolation to prevent further spread. Airlines have been asked to assess their own risk and accordingly formulate policies for crew of flights whose members tested positive.[38]</p>\r\n\r\n<p>Shahid Gangalal National Heart Center suspends services after detection of virus in patients: All services except emergencies have been halted at Shahid Gangalal National Heart Center in Kathmandu after Coronavirus was detected in some patients. Some 20 patients seeking surgery at the hospital were tested and seven of the test results came back positive. An official at the Health Ministry said that the health workers who were exposed to the patients will be tested, and the in-door patients will be managed before the hospital is allowed to resume its services. Health Ministry officials, who have so far claimed that the infection does not spread from asymptomatic patients, are now saying there is a high risk of community transmission.[39]</p>\r\n\r\n<p>A destructive monsoon season to collide with the pandemic: In the first few weeks of the monsoon onset in Nepal, 35 people have lost their lives due to landslides in different areas. This is indicative of a destructive rainy season for the country already hurt by the COVID-19 pandemic and a locust invasion. The government’s monsoon action plan warns that the country might be battered with extreme water-induced events in the last week of June and the first week of August, which is also expected to be the time of peak COVID-19 cases.[40]</p>\r\n\r\n<p>Protesters call of hunger strike following a12-point agreement with Health Ministry: The ‘Enough is Enough’ youth-led campaign has called their hunger strike off following a 12-point agreement with the Health Ministry on July 6. The campaign began with a protest on June 9 in front of the Prime Minister’s residence. The protesters had asked for better quarantine conditions, more PCR testing and financial transparency over COVID-19 spending. Discontinuing the use of rapid diagnostic tests and increase polymerase chain reaction testing to detect the presence of the virus had been a major demand of the campaign which has been included in the agreement.[41]</p>\r\n\r\n<h6>Provincial and Local Governments</h6>\r\n\r\n<p>Budget and development issues: On July 3, the Ministry of Economic Affairs and Planning, Gandaki Pradesh, passed their annual budget worth Rs 38.84 billion for the upcoming fiscal year 2077/78 and allocated Rs 14.34 billion for recurrent expenditure and Rs 19.99 billion for capital expenditure. The budget focuses on a specific program to fight the COVID-19 pandemic, including infrastructure, agriculture, education, tourism sectors.[42]</p>\r\n\r\n<p>Bagmati Pradesh passed the Appropriation Bill by the majority of the ruling Nepal Communist Party (NCP) for the upcoming fiscal year 2077/78. However, all opposition parties disagreed on the Appropriation Bill claiming that it was not made public for the discussion among the MPs. Bagmati Pradesh presented a budget of Rs 51.42 billion for the upcoming fiscal year. Apart from this, the Chief Minister of the province, Dormani Paudel, also appealed everyone to support the government’s task.[43]</p>\r\n\r\n<p>Agriculture relief packages: The Ministry of Land Management, Agriculture and Cooperative, Province 1, is going to distribute Rs 100.39 million to the farmers affected by the lockdown. Similarly, the provincial government allocated Rs 50.34 million for animal husbandry and Rs 40.69 million for agriculture as a financial support for the current fiscal year 2076/77.[44]</p>\r\n\r\n<p>The Ministry of Land Management, Agriculture, and Cooperative, Province 2, will pay four months interest of farmers affected by the ongoing lockdown. The interest will be paid to the farmers who have taken loans of up to Rs 500,000. The provincial government has allocated Rs 360 million for this program. The ministry said that 80,000 farmers will benefit from the program.[45]</p>\r\n\r\n<p>Coronavirus response: The number of people staying in quarantine centers has been decreasing in Province 2. On July 2, the number of people in quarantine across eight districts has reduced to 3,770. However, two weeks ago, 15,581 people were being quarantined.[46]</p>\r\n\r\n<p>In Province 5, the Rapti Institute of Health Sciences in Ghorahi was charging Rs 5,500 per person for PCR tests. But the institute stopped their testing from July 4 after the local level refused to pay. The institute has also sent a letter to all local level authorities asking them to pay for the Coronavirus test. [47]</p>\r\n\r\n<p>According to the Ministry of Social Development of Sudurpashchim Pradesh, there has been rapid increase of COVID-19 patients within a week. There were more than 2,000 cases in a week.[48]</p>\r\n\r\n<h6>Gender, Social Inclusion, Human Rights, and Migration</h6>\r\n\r\n<p>NHRC recommends to legalise same-sex marriage: The report released by National Human Rights Commission on July 1 recommended the government to legally begin acknowledging same-sex marriages and open legal channels thereof. The discrimination faced by transgender community in society is persistent and the government lacks in taking sufficient action to ensure their rights. The report recommends the federal government to enforce a Special Act, include provisions for ‘positive discrimination’, provide scholarship and hostel facilities to the transgender community until higher education, and establish funds to support individuals willing to undergo gender reassignment surgery.[49]</p>\r\n\r\n<p>Custodial death: Durgesh Yadav, who was accused of rape, was found hanged in the police custody of Lalitpur Metropolitan Police Range on July 1. The accused was taken to Patan Hospital following the incident, where the doctors pronounced him dead. The man, after being arrested on July 4, was kept in a separate room for COVID-19 test.[50]</p>\r\n\r\n<p>Ministry develops guidelines for rehab centres: The Ministry of Women, Children and Senior Citizens came up with guidelines for functioning of rehabilitation centres for the safety of vulnerable persons from COVID-19. The guidelines will remain in force until Nepal is pronounced Coronavirus free. As per the guidelines, all the centres are required to incorporate precautionary measures to curb the spreading of the virus.[51]</p>\r\n\r\n<h6>Migrant workers</h6>\r\n\r\n<p>Repatriation of migrant workers: As per the new guidelines in effect, 1,242 stranded Nepali migrant workers stranded overseas were repatriated on July 2, via eight chartered flights.[52] 917 Nepali nationals and two dead bodied were brought back on July 3.[53] A total of 1,082 migrant workers were rescued from five destinations via seven flights on July 4.[54]</p>\r\n\r\n<p>Issuance of labour permits to migrant workers: The Ministry of Labour, Employment, and Social Security decided to recommence the issuance of labour permits on July 3, to migrant workers who are stuck in the country because of job breaks and those who were willing to go back. However, the labour migrant experts heavily criticised the government’s decision. [55] The workers are required to undertake a COVID-19 test and comply with the health standard protocol to acquire labour permits.[56]</p>\r\n\r\n<p>Rise in exploitation risk for women migrant workers amidst pandemic: Labour migration experts and human rights workers say that women migrant workers are facing harsher impact of COVID-19 pandemic amongst other migrant workers in several destination countries. Ashok Rana, Chairperson of National Network for Safe Migration, said that the condition of women migrant workers is critical, and they are at a higher risk of contracting Coronavirus along with other kinds of exploitation.[57]</p>\r\n\r\n<p>ILO doles out grants to support migrant workers: On July 4, the ILO confirmed to provide Rs 51 million as grant to the Non-resident Nepali Association (NRNA) to repatriate stranded Nepali nationals from Malaysia, Qatar, United Arab Emirates, and Saudi Arabia owing to COVID-19 pandemic. The fund will further be utilised to study the impact of COVID-19 in the labour market.[58]</p>\r\n\r\n<h6>Image by John Hain from Pixabay</h6>\r\n\r\n<ol>\r\n	<li><a href=\"https://www.nayapatrikadaily.com/news-details/46612/2020-07-04\"><span style=\"color:#FF0000;\">https://www.nayapatrikadaily.com/news-details/46566/2020-07-03</span></a></li>\r\n	<li><a href=\"http://https://www.nayapatrikadaily.com/news-details/46612/2020-07-04\"><span style=\"color:#FF0000;\">http://https://www.nayapatrikadaily.com/news-details/46612/2020-07-04</span></a></li>\r\n	<li><a href=\"http://] https://kathmandupost.com/politics/2020/07/04/oli-asks-party-s-sister-organisations-to-help-create-a-favourable-environment-for-him\"><span style=\"color:#FF0000;\">http://] https://kathmandupost.com/politics/2020/07/04/oli-asks-party-s-sister-organisations-to-help-create-a-favourable-environment-for-him</span></a></li>\r\n	<li><a href=\"https://ekantipur.com/news/2020/07/04/159383741296651216.html\"><span style=\"color:#FF0000;\">https://ekantipur.com/news/2020/07/04/159383741296651216.html</span></a></li>\r\n	<li><a href=\"http://https://thehimalayantimes.com/kathmandu/pm-oli-dahal-to-continue-dialogue-tomorrow-forms-an-unofficial-task-force/\"><span style=\"color:#FF0000;\">http://https://thehimalayantimes.com/kathmandu/pm-oli-dahal-to-continue-dialogue-tomorrow-forms-an-unofficial-task-force/</span></a></li>\r\n	<li><a href=\"http://https://nagariknews.nagariknetwork.com/politics/264711-1594008332.html\"><span style=\"color:#FF0000;\">http://https://nagariknews.nagariknetwork.com/politics/264711-1594008332.html</span></a></li>\r\n	<li><a href=\"https://deshsanchar.com/2020/07/08/380683/\"><span style=\"color:#FF0000;\">https://deshsanchar.com/2020/07/08/380683/</span></a></li>\r\n	<li><a href=\"http://https://www.onlinekhabar.com/2020/07/879037\"><span style=\"color:#FF0000;\">http://https://www.onlinekhabar.com/2020/07/879037</span></a></li>\r\n	<li><a href=\"http://https://thehimalayantimes.com/kathmandu/dahal-nepal-khanal-visit-prez-bhandari-in-shital-niwas/\"><span style=\"color:#FF0000;\">http://https://thehimalayantimes.com/kathmandu/dahal-nepal-khanal-visit-prez-bhandari-in-shital-niwas/</span></a></li>\r\n	<li><a href=\"https://www.nayapatrikadaily.com/news-details/46711/2020-07-06\"><span style=\"color:#FF0000;\">https://www.nayapatrikadaily.com/news-details/46711/2020-07-06</span></a></li>\r\n	<li><a href=\"http://https://myrepublica.nagariknetwork.com/news/chinese-envoy-holds-meeting-with-ncp-leader-nepal-ahead-of-crucial-standing-committee-of-the-ruling-party/\"><span style=\"color:#FF0000;\">http://https://myrepublica.nagariknetwork.com/news/chinese-envoy-holds-meeting-with-ncp-leader-nepal-ahead-of-crucial-standing-committee-of-the-ruling-party/</span></a></li>\r\n	<li><a href=\"http://https://www.onlinekhabar.com/2020/07/878945\"><span style=\"color:#FF0000;\">http://https://www.onlinekhabar.com/2020/07/878945</span></a></li>\r\n	<li><a href=\"http://https://www.setopati.com/politics/210736\"><span style=\"color:#FF0000;\">http://https://www.setopati.com/politics/210736</span></a></li>\r\n	<li><a href=\"https://ekantipur.com/news/2020/07/07/159411548506483766.html\"><span style=\"color:#FF0000;\">https://ekantipur.com/news/2020/07/07/159411548506483766.html</span></a></li>\r\n	<li><a href=\"http://https://www.nayapatrikadaily.com/news-details/46826/2020-07-08\"><span style=\"color:#FF0000;\">http://https://www.nayapatrikadaily.com/news-details/46826/2020-07-08</span></a></li>\r\n	<li><a href=\"https://deshsanchar.com/2020/07/07/380542/\"><span style=\"color:#FF0000;\">https://deshsanchar.com/2020/07/07/380542/</span></a></li>\r\n	<li><a href=\"http://https://deshsanchar.com/2020/07/07/380519/\"><span style=\"color:#FF0000;\">http://https://deshsanchar.com/2020/07/07/380519/</span></a></li>\r\n	<li><a href=\"http://https://deshsanchar.com/2020/07/07/380494/\"><span style=\"color:#FF0000;\">http://https://deshsanchar.com/2020/07/07/380494/</span></a></li>\r\n	<li>&nbsp;</li>\r\n</ol>\r\n<br />\r\n&nbsp;', NULL, 2792, NULL, NULL, 'CESIF Nepal', '/uploads/banner_image/1670580577_handcuffed-1251664_1280.jpg', NULL, NULL, NULL, '2022-07', '2022-07-09', NULL, '1', '0', NULL, NULL, NULL, '2022-12-09 21:54:37', '2022-12-09 22:01:28'),
